import React from 'react'
import Rubicon from '../Images/wrangler.jpeg'
import TripDetailsCard from '../Listpage/TripDetailsCard'
import { Link } from 'react-router-dom'

const SelectedFullCabDetails = () => {
    const cars = [
          {
            name: "Jeep Rubicon",
            category: "Premium SUV",
            image: Rubicon,
            seats: 6,
            price: 13000,
            discount: 15,
            originalPrice: 15000,
            transmission: "Manual",
            hasAC: true,
            rating: 4.7,
            reviews: 189,
            features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
                "Comfortable & Spacious Seating",
                "Female Passenger Safety Standards"],
        }
    
    
        ]
  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
        {/* Tabs Section */}
        <div className="w-full lg:w-2/3">
          

          {/* Car Cards */}
          {cars.map((car, index) => (
            <div
              key={index}
              className="bg-white rounded-lg mt-6 shadow-md p-4 mb-4 flex flex-col sm:flex-row gap-4"
            >
              {/* Car Image */}
              <img
                src={car.image || "/placeholder.svg"}
                alt={car.name}
                className="w-full sm:w-1/3 object-cover rounded-lg"
              />

              {/* Car Details */}
              <div className="flex flex-col sm:flex-row justify-between w-full">
                <div className="flex-grow">
                  <h3 className="text-lg sm:text-xl font-bold text-gray-800">
                    {car.name}
                  </h3>
                  <p className="text-gray-900 mb-2 text-sm sm:text-base">
                    {car.seats} Seater
                  </p>
                  <ul className="text-gray-700 text-sm mb-4">
                    {car.features.map((feature, idx) => (
                      <li key={idx}>• {feature}</li>
                    ))}
                  </ul>
                </div>

                {/* Additional Car Details */}
                <div className="flex flex-col items-center justify-center text-center">
                  <p className="text-base sm:text-lg font-semibold">{car.date}</p>
                  <p className="text-gray-600 text-sm sm:text-base">{car.time}</p>
                </div>

                {/* Pricing and Actions */}
                <div className="flex flex-col items-end justify-between text-right ">
                  <p className=" text-grey-900 mt-2 sm:mt-5 font-sans text-sm sm:text-base bg-green-300 rounded-full px-3 py-1">
                    {car.category}
                  </p>
                  <div className="inline-block px-3 py-1 text-blue-600 rounded-full text-sm font-medium mt-1">
                    {car.discount}% OFF!
                  </div>
                  <p className="text-gray-400 line-through text-xl">
                    ₹{car.originalPrice}
                  </p>
                  <p className="text-xl sm:text-2xl font-bold text-blue-600">
                    ₹{car.price}/-
                  </p>
                  {/* <button className="mt-4 bg-gradient-to-r from-blue-300 to-blue-400 text-white py-2 px-4 sm:px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto text-sm sm:text-base">
                    <Link to="#">Book Now</Link>
                  </button> */}
                </div>
              </div>
            </div>
          ))}


<div className="p-4">
          <div className="w-full max-w-3xl mx-auto mt-10">
            <form onSubmit={handleSubmit} className="bg-gray-50 p-4 md:p-8 shadow-lg rounded-lg">
              <h2 className="text-xl md:text-2xl font-semibold text-center text-gray-900 mb-6">Passenger Details</h2>

              {/* Passenger Details Grid */}
              <div
                className={`grid gap-6 ${selectedSeats.length === 1 ? "grid-cols-1" : "grid-cols-1 md:grid-cols-2"}`}
              >
                {Object.entries(passengers).map(([seatId, passenger], index) => (
                  <div key={seatId} className="bg-white p-4 md:p-6 rounded-md shadow-sm border space-y-4 flex flex-col">
                    <h3 className="text-lg font-medium text-gray-800">Passenger for Seat {seatId}</h3>

                    {/* Passenger Name */}
                    <div>
                      <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                        Name
                      </label>
                      <input
                        type="text"
                        id={`name-${seatId}`}
                        value={passenger.name}
                        onChange={(e) =>
                          setPassengers((prev) => ({
                            ...prev,
                            [seatId]: { ...prev[seatId], name: e.target.value },
                          }))
                        }
                        required
                        className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                      />
                    </div>

                    {/* Main Passenger Checkbox */}
                    <div>
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={mainPassenger === seatId}
                          onChange={() => handleMainPassengerChange(seatId)}
                          className="form-checkbox h-5 w-5 text-blue-600"
                        />
                        <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                      </label>
                    </div>

                    {mainPassenger === seatId && (
                      <div className="space-y-4">
                        {/* Email and Phone in Flex */}
                        <div className="flex flex-col sm:flex-row gap-4">
                          {/* Email */}
                          <div className="flex-1">
                            <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                              Email
                            </label>
                            <input
                              type="email"
                              id={`email-${seatId}`}
                              value={passenger.email}
                              onChange={(e) =>
                                setPassengers((prev) => ({
                                  ...prev,
                                  [seatId]: { ...prev[seatId], email: e.target.value },
                                }))
                              }
                              required
                              className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                            />
                          </div>

                          {/* Phone */}
                          <div className="flex-1">
                            <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700">
                              Phone Number
                            </label>
                            <input
                              type="tel"
                              id={`phone-${seatId}`}
                              value={passenger.phone}
                              onChange={(e) =>
                                setPassengers((prev) => ({
                                  ...prev,
                                  [seatId]: { ...prev[seatId], phone: e.target.value },
                                }))
                              }
                              required
                              className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                            />
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Gender Selection */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700">Gender</label>
                      <div className="flex flex-wrap items-center gap-4 mt-2">
                        {["Male", "Female", "Other", "Prefer not to say"].map((gender) => (
                          <label key={gender} className="flex items-center">
                            <input
                              type="radio"
                              name={`gender-${seatId}`}
                              value={gender}
                              checked={passenger.gender === gender}
                              onChange={(e) =>
                                setPassengers((prev) => ({
                                  ...prev,
                                  [seatId]: { ...prev[seatId], gender: e.target.value },
                                }))
                              }
                              className="form-radio h-4 w-4 text-blue-600"
                            />
                            <span className="ml-2 text-sm text-gray-700">{gender}</span>
                          </label>
                        ))}
                      </div>
                      {passenger.gender === "Other" && (
                        <input
                          type="text"
                          placeholder="Enter your gender"
                          value={passenger.customGender || ""}
                          onChange={(e) =>
                            setPassengers((prev) => ({
                              ...prev,
                              [seatId]: { ...prev[seatId], customGender: e.target.value },
                            }))
                          }
                          className="mt-2 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                        />
                      )}
                    </div>

                    
                  </div>
                ))}
              </div>

              {/* Error Message */}
              {error && <div className="text-red-500 text-center mt-4">{error}</div>}

              {/* No Seats Selected Message */}
            

              {/* Submit Button */}
              <div className="text-center mt-8">
                <button
                  type="submit"
                  className="px-6 py-3 text-sm font-medium rounded-md shadow "
                >
                  Book Seats
                </button>
              </div>
            </form>
          </div>
        </div>
        </div>
        

        {/* Trip Details Section */}
        <div className="w-full lg:w-1/3 rounded-lg p-4">
          <TripDetailsCard />
        </div>
      </div>
  )
}

export default SelectedFullCabDetails
