import { useState } from "react";

export default function BookingForm() {
  const [formData, setFormData] = useState({
    from: "",
    to: "",
    pickupStreet: "",
    dropStreet: "",
    date: "",
  });

  const [error, setError] = useState(""); // For validation errors

  const locations = ["Location 1", "Location 2", "Location 3", "Location 4"];
  const streets = ["Street 1", "Street 2", "Street 3", "Street 4"]; // Example streets for each location

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for empty fields
    if (
      !formData.from ||
      !formData.to ||
      !formData.pickupStreet ||
      !formData.dropStreet ||
      !formData.date
    ) {
      setError("All fields are required.");
      return;
    }

    // Check if 'From' and 'To' locations are the same
    if (formData.from === formData.to) {
      setError("From and To locations cannot be the same.");
      return;
    }

    // Check if date is valid
    const selectedDate = new Date(formData.date);
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Reset to start of today
    if (selectedDate < today) {
      setError("Date cannot be in the past.");
      return;
    }

    setError(""); // Clear any previous errors
    console.log("Form submitted:", formData);
  };

  const today = new Date();
  const currentDate = today.toISOString().split("T")[0];

  return (
    <div className="max-w-4xl mx-auto px-4">
      <div className="bg-white rounded-3xl shadow-lg p-8 md:p-10 -mt-20 relative z-10">
        <h2 className="text-2xl md:text-3xl font-semibold text-center mb-8">
          Bookings made easy
        </h2>
        {error && (
          <p className="text-red-600 text-center mb-4 font-semibold">{error}</p>
        )}
        <form
          onSubmit={handleSubmit}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4"
        >
          <select
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.from}
            onChange={(e) => setFormData({ ...formData, from: e.target.value })}
          >
            <option value="">Select From Location</option>
            {locations.map((location) => (
              <option key={location} value={location}>
                {location}
              </option>
            ))}
          </select>

          <select
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.to}
            onChange={(e) => setFormData({ ...formData, to: e.target.value })}
          >
            <option value="">Select To Location</option>
            {locations.map((location) => (
              <option key={location} value={location}>
                {location}
              </option>
            ))}
          </select>

          

          <input
            type="date"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.date}
            min={currentDate}
            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
          />

          <select
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.dropStreet}
            onChange={(e) =>
              setFormData({ ...formData, dropStreet: e.target.value })
            }
          >
            <option value="">Select Drop Street</option>
            {streets.map((street) => (
              <option key={street} value={street}>
                {street}
              </option>
            ))}
          </select>

          <button
            type="submit"
            className="bg-[#1a1147] text-white rounded-lg px-6 py-3 hover:bg-[#2a1b67] transition-colors"
          >
            Search for Cabs
          </button>
        </form>
      </div>
    </div>
  );
}
