import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BookingForm from '../Home/bookingForm';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null); // State for error message
  const [bookingData, setBookingData] = useState(null); // Store booking form data
  const navigate = useNavigate();

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!mainPassenger) {
      setError('There should be one main passenger! Please fill.');
      return;
    }

    if (!bookingData) {
      setError('Please fill out the booking form first.');
      return;
    }

    setError(null); // Clear any previous error
    setShowModal(true); // Show the modal on form submission
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats'); // Navigate to the Seats page
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <div className="flex flex-col md:flex-row gap-8 p-4">
      {/* Booking Form */}
      <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
        <div className="bg-gray-100 px-4 py-2">
          <h2 className="text-lg font-semibold">Booking Information</h2>
        </div>
        <BookingForm className="mt-0" buttonLabel="Modify" setBookingData={setBookingData} />
        <div className="p-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            {Object.entries(passengers).map(([seatId, passenger]) => (
              <div key={seatId} className="space-y-2">
                <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                  Passenger Name for Seat {seatId}
                </label>
                <input
                  type="text"
                  id={`name-${seatId}`}
                  value={passenger.name}
                  onChange={(e) =>
                    setPassengers((prev) => ({
                      ...prev,
                      [seatId]: { ...prev[seatId], name: e.target.value },
                    }))
                  }
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                />
                <label className="inline-flex items-center mt-2">
                  <input
                    type="checkbox"
                    checked={mainPassenger === seatId}
                    onChange={() => handleMainPassengerChange(seatId)}
                    className="form-checkbox text-blue-600"
                  />
                  <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                </label>
              </div>
            ))}
            {error && (
              <div className="text-red-600 text-sm font-medium">
                {error}
              </div>
            )}
            {selectedSeats.length > 0 && (
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Book Seats
              </button>
            )}
          </form>
        </div>
      </div>

      {/* Modal */}
      {showModal && bookingData && (
        <div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white w-11/12 max-w-3xl rounded-xl shadow-2xl p-8 relative animate-fade-in">
            <button
              onClick={closeModal}
              className="absolute top-3 right-3 text-gray-700 hover:text-gray-900 bg-gray-200 rounded-full p-2 shadow transition hover:scale-110"
            >
              &times;
            </button>
            <h3 className="text-2xl font-bold text-gray-800 mb-6 border-b pb-2">
              Booking Details
            </h3>
            <div className="space-y-3">
              <p className="text-gray-600">
                <span className="font-semibold">From Location:</span> {bookingData.from}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">To Location:</span> {bookingData.to}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Pickup Location:</span> {bookingData.pickupStreet}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Drop Location:</span> {bookingData.dropStreet}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Date of Trip:</span> {bookingData.date}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Seats Booked:</span>{' '}
                {selectedSeats.map((seat) => seat.id).join(', ')}
              </p>
              <p className="text-gray-600 text-lg font-medium">
                <span className="font-semibold">Total Amount Payable:</span> Rs. {totalAmount}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatList;
