import React from 'react';
import BookingForm from '../Home/bookingForm';
import Crysta from '../Images/cryy.jpg';
import { Link } from 'react-router-dom';
import TripDetailsCard from './TripDetailsCard';

const CarList = () => {
  const carData = [
    {
      id: 1,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 2,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 3,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      seatsLeft: 5,
      image: Crysta,
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
  ];

  const cabs = [
      {
        name: "Toyota Innova",
        features: ["Air Conditioned Luxury SUV", "Comfortable & Spacious Seating", "Female Passenger Safety Standards"],
        seatsLeft: 3,
        price: 1200,
        image: Crysta,
        bookedSeats: [2, 5], // Seat numbers that are already booked
      },
      {
        name: "Maruti Suzuki Ertiga",
        features: ["Fuel Efficient MPV", "Comfortable Seating for 7", "24/7 Roadside Assistance"],
        seatsLeft: 5,
        price: 1000,
        image: Ertiga,
        bookedSeats: [2],
      },
      {
        name: "Mahindra XUV500",
        features: ["Powerful SUV for Hill Drives", "Advanced Safety Features", "Panoramic Sunroof"],
        seatsLeft: 5,
        price: 1500,
        image: Xuv,
        bookedSeats: [2],
      },
    ];
  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      {/* Trip Details Section */}


      {/* Car List Section */}
      <div className="w-full lg:w-2/3">
        {/* Tabs */}
        {/* <div className="flex space-x-4 mb-4">
          <button className="px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
            Shared Cars
          </button>
          <button className="px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
            Full Cars
          </button>
        </div> */}

        {/* Car Cards */}
        {carData.map((car) => (
          <div
            key={car.id}
            className="bg-white rounded-lg shadow-md p-4 mb-4 flex flex-col lg:flex-row gap-4"
          >
            <img
              src={car.image} // Replace with actual car image URL
              alt={car.name}
              className="w-full lg:w-1/3 object-cover rounded-lg"
            />
            <div className="flex flex-col lg:flex-row justify-between w-full">
              <div className="flex-grow">
                <h3 className="text-xl font-bold">{car.name}</h3>
                <p className="text-gray-500 mb-2">{car.seats} Seater</p>
                <ul className="text-gray-700 text-sm mb-4">
                  {car.features.map((feature, index) => (
                    <li key={index}>• {feature}</li>
                  ))}
                </ul>
              </div>
              <div className="flex flex-col items-end justify-between text-right">
                <p className="text-gray-500 mt-14">Seats Left: {car.seatsLeft}</p>
                <p className="text-xl font-bold mt-0">₹{car.price}/-</p>
                <button className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700">
                  <Link to="/Seats">Select Seat</Link>
                </button>
              </div>
            </div>
          </div>
        ))}


      </div>

      <div className="w-full lg:w-1/4  rounded-lg p-4 ">
        {/* <h2 className="text-xl font-bold mb-4">Trip Details</h2> */}
        <br />
        {/* <BookingForm className="bg-blue-500 mt-0" buttonLabel="Modify"/> */}
        <TripDetailsCard />
      </div>
    </div>
  );
};

export default CarList;
