import React, { useState } from 'react';
import { FaArrowRight } from 'react-icons/fa';

function BookingForm() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [error, setError] = useState(''); // State to store error message
  const [formData, setFormData] = useState({
      from: "",
      to: "",
      pickupPoint: "",
      dropPoint: "",
      date: "",
    })

  const handleSubmit = (e) => {
    e.preventDefault();
    if (from === to) {
      setError('Pickup and drop points are the same'); // Set error message
    } else {
      setError(''); // Clear error message
      console.log('Search submitted:', { from, to, date, time });
      // Proceed with form submission logic
    }
  };

  // Get today's date in YYYY-MM-DD format
  const today = new Date().toISOString().split('T')[0];

  // Get the current time in 24-hour format (HH:mm)
  const currentDate = new Date();
  currentDate.setHours(currentDate.getHours() + 2); // Add 2 hours
  const currentTime = currentDate.toTimeString().slice(0, 5); // Format: HH:mm

  return (
    <div className="max-w-4xl mx-auto px-4">
      <div className="bg-white rounded-3xl shadow-lg p-8 md:p-10 -mt-20 relative z-10">
        <h2 className="text-2xl md:text-3xl font-semibold text-center mb-8">Bookings made easy</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <input
            type="text"
            placeholder="From"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.from}
            onChange={(e) => setFormData({ ...formData, from: e.target.value })}
          />
          <input
            type="text"
            placeholder="To"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.to}
            onChange={(e) => setFormData({ ...formData, to: e.target.value })}
          />
          <input
            type="text"
            placeholder="Select Pickup Point"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.pickupPoint}
            onChange={(e) => setFormData({ ...formData, pickupPoint: e.target.value })}
          />
          <input
            type="text"
            placeholder="Select Drop Point"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.dropPoint}
            onChange={(e) => setFormData({ ...formData, dropPoint: e.target.value })}
          />
          <input
            type="date"
            className="bg-gray-100 rounded-lg px-4 py-3 w-full"
            value={formData.date}
            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
          />
          <button
            type="submit"
            className="bg-[#1a1147] text-white rounded-lg px-6 py-3 hover:bg-[#2a1b67] transition-colors"
          >
            Search for Cabs
          </button>
        </form>
      </div>
    </div>

  );
}

export default BookingForm;
