import React, { useState, useEffect } from 'react';
import { FaArrowRight } from 'react-icons/fa';

function BookingForm() {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Search submitted:', { from, to, date, time });
  };

  // Get today's date in YYYY-MM-DD format
  const today = new Date().toISOString().split('T')[0];

  // Get the current time in 24-hour format (HH:mm)
  const currentTime = new Date().toTimeString().slice(0, 5); // Format: HH:mm

  // Time step for 15 minutes interval
  const timeStep = 15; 

  // Handle change in time input
  const handleTimeChange = (e) => {
    const inputTime = e.target.value;
    const [hours, minutes] = inputTime.split(":");

    // Validate the minute value to ensure it's one of the allowed values (00, 15, 30, 45)
    if (minutes === "00" || minutes === "15" || minutes === "30" || minutes === "45") {
      setTime(inputTime);
    } else {
      setTime("");
    }
  };

  useEffect(() => {
    // Automatically set the time to the next valid 15-minute interval
    if (!time) {
      let currentDate = new Date();
      let minutes = currentDate.getMinutes();
      let roundedMinutes = Math.floor(minutes / timeStep) * timeStep; 
      let nextValidMinutes = (roundedMinutes === 60 ? 0 : roundedMinutes);
      let nextValidTime = `${currentDate.getHours().toString().padStart(2, '0')}:${nextValidMinutes.toString().padStart(2, '0')}`;

      setTime(nextValidTime);
    }
  }, [time]);

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-gradient-to-br from-indigo-50 via-white to-indigo-100 p-10 rounded-2xl shadow-2xl max-w-4xl mx-auto border border-indigo-200"
    >
      <h2 className="text-3xl font-extrabold text-indigo-700 mb-8 text-center">
        Book Your Ride
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 items-end">
        <div>
          <label htmlFor="from" className="block text-sm font-semibold text-gray-700">
            From
          </label>
          <select
            id="from"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            className="mt-2 block w-full rounded-lg border-gray-300 shadow-md focus:border-indigo-400 focus:ring focus:ring-indigo-300 focus:ring-opacity-50 bg-white text-lg px-4 py-3"
          >
            <option value="">Select Pickup Point</option>
            <option value="point1">Bangalore</option>
            <option value="point2">Chennai</option>
            <option value="point3">Pune</option>
          </select>
        </div>

        {/* Arrow Icon with black color */}
        <div className="hidden sm:flex justify-center items-center text-black">
          <FaArrowRight size={30} />
        </div>

        <div>
          <label htmlFor="to" className="block text-sm font-semibold text-gray-700">
            To
          </label>
          <select
            id="to"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            className="mt-2 block w-full rounded-lg border-gray-300 shadow-md focus:border-indigo-400 focus:ring focus:ring-indigo-300 focus:ring-opacity-50 bg-white text-lg px-4 py-3"
          >
            <option value="">Select Drop Point</option>
            <option value="point1">Bangalore</option>
            <option value="point2">Chennai</option>
            <option value="point3">Pune</option>
          </select>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 mt-6">
        <div>
          <label htmlFor="date" className="block text-sm font-semibold text-gray-700">
            Date
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            min={today} // Disable past dates
            className="mt-2 block w-full rounded-lg border-gray-300 shadow-md focus:border-indigo-400 focus:ring focus:ring-indigo-300 focus:ring-opacity-50 bg-white text-lg px-4 py-3"
          />
        </div>
        <div>
          <label htmlFor="time" className="block text-sm font-semibold text-gray-700">
            Time
          </label>
          <input
            type="time"
            id="time"
            value={time}
            onChange={handleTimeChange}
            className="mt-2 block w-full rounded-lg border-gray-300 shadow-md focus:border-indigo-400 focus:ring focus:ring-indigo-300 focus:ring-opacity-50 bg-white text-lg px-4 py-3"
            step={timeStep * 60} // Step for 15-minute intervals
          />
        </div>
      </div>

      <div className="mt-8">
        <button
          type="submit"
          className="w-full sm:w-auto bg-gradient-to-r from-indigo-500 to-indigo-600 text-white font-semibold py-3 px-8 rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:ring-opacity-50 transition transform hover:scale-105"
        >
          Search for Cabs
        </button>
      </div>
    </form>
  );
}

export default BookingForm;
