import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Crysta from "../Images/cryy.jpg";
import Creta from "../Images/creta.jpeg";
import Etios from "../Images/etios.png";
import Gwagon from "../Images/Gwagon.jpg";
import Gurkha from "../Images/Gurkha.jpg";
import Xuv from "../Images/Xuv500.jpg";
import TripDetailsCard from "./TripDetailsCard";

const CarList = () => {
  const initialCarData = [
    {
      id: 1,
      name: "Mercedes G-wagon",
      seats: 6,
      price: 5500,
      image: Gwagon,
      seatsLeft: 1,
      Date: "08/01/2025",
      time: "5:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 2,
      name: "Force Gurkha",
      seats: 6,
      price: 4000,
      image: Gurkha,
      seatsLeft: 2,
      Date: "08/01/2025",
      time: "6:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 3,
      name: "Mahindra Xuv500",
      seats: 6,
      price: 3000,
      seatsLeft: 3,
      image: Xuv,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 4,
      name: "Toyota Innova",
      seats: 6,
      price: 3200,
      seatsLeft: 4,
      image: Crysta,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 5,
      name: "Hyundai Creta",
      seats: 6,
      price: 2500,
      seatsLeft: 5,
      image: Creta,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 6,
      name: "Toyota Etios",
      seats: 6,
      price: 2200,
      seatsLeft: 6,
      image: Etios,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    }
    // Additional car objects...
  ];

  const [carData, setCarData] = useState(initialCarData);
  const [sortConfig, setSortConfig] = useState({ key: null, ascending: true });

  const handleSort = (key) => {
    const isAscending =
      sortConfig.key === key ? !sortConfig.ascending : true; // Toggle sort order
    const sortedData = [...carData].sort((a, b) => {
      if (key === "time") {
        const timeA = new Date(`1970-01-01T${a.time}`);
        const timeB = new Date(`1970-01-01T${b.time}`);
        return isAscending ? timeA - timeB : timeB - timeA;
      } else if (key === "price" || key === "seatsLeft") {
        return isAscending ? a[key] - b[key] : b[key] - a[key];
      }
      return 0;
    });

    setCarData(sortedData);
    setSortConfig({ key, ascending: isAscending });
  };


  const location = useLocation();
  // const isActive = ;

  return (
    <>
      <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
        <div className="w-full lg:w-2/3">
          {/* Tabs */}
          <div className="flex space-x-4 mb-4">
          <button
      className={`px-4 py-2 text-white rounded-lg text-sm sm:text-base transition duration-300 ${
        location.pathname === "/sharedcabs"
          ? "bg-gradient-to-r from-purple-500 to-pink-500 shadow-md"
          : "bg-gradient-to-r from-blue-300 to-blue-400 hover:bg-gray-300"
      }`}
    >
      <Link to="/sharedcabs">Shared Cars</Link>
    </button>

    <button
      className={`px-4 py-2 text-white rounded-lg text-sm sm:text-base transition duration-300 ${
        location.pathname === "/fullcars"
          ? "bg-gradient-to-r from-purple-500 to-pink-500 shadow-md"
          : "bg-gradient-to-r from-blue-300 to-blue-400 hover:bg-gray-300"
      }`}
    >
       <Link to="/fullcars">Full Cars</Link>
    </button>
          
          </div>

          {/* Table Headers */}
          <div className="bg-gray-50 w-full flex text-center">
           

            <div
              // onClick={() => handleSort("time")}
              className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider   flex items-center flex-1"
            >
              {/* Time
              <span className="ml-1">
                {sortConfig.key === "time"
                  ? sortConfig.ascending
                    ? "▲"
                    : "▼"
                  : "⇵"}
              </span> */}
            </div>

            <div
              onClick={() => handleSort("seatsLeft")}
              className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100 flex items-center flex-1"
            >
              Seats Left
              <span className="ml-1">
                {sortConfig.key === "seatsLeft"
                  ? sortConfig.ascending
                    ? "▲"
                    : "▼"
                  : "⇵"}
              </span>
            </div>

            <div
              onClick={() => handleSort("price")}
              className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100 flex items-center flex-1"
            >
              Fare
              <span className="ml-1">
                {sortConfig.key === "price"
                  ? sortConfig.ascending
                    ? "▲"
                    : "▼"
                  : "⇵"}
              </span>
            </div>
          </div>

          {/* Car Cards */}
          {carData.map((car) => (
            <div
              key={car.id}
              className="bg-white rounded-lg shadow-md p-4 mb-4 flex flex-col sm:flex-row gap-4"
            >
              {/* Car Image */}
              <img
                src={car.image}
                alt={car.name}
                className="w-full sm:w-1/3 object-cover rounded-lg"
              />

              {/* Car Details */}
              <div className="flex flex-col sm:flex-row justify-between w-full">
                <div className="flex-grow">
                  <h3 className="text-lg sm:text-xl font-bold">{car.name}</h3>
                  <p className="text-gray-900 mb-2 text-sm sm:text-base">
                    {car.seats} Seater
                  </p>
                  <ul className="text-gray-700 text-sm mb-4">
                    {car.features.map((feature, index) => (
                      <li key={index}>• {feature}</li>
                    ))}
                  </ul>
                </div>

                <div className="flex flex-col items-center justify-center text-center">
                  <h1 className="text-base sm:text-lg font-semibold">
                    {car.Date}
                  </h1>
                  <h4 className="text-gray-600 text-sm sm:text-base">
                    {car.time}
                  </h4>
                </div>

                <div className="flex flex-col items-end justify-between text-right">
                
                  <p className="text-gray-900 mt-2 sm:mt-5 font-bold text-sm sm:text-base">
                    Seats Left: {car.seatsLeft}
                  </p>
                  <p className="text-xl sm:text-2xl font-bold text-blue-600">
                    ₹{car.price}/-
                  </p>
                  <p className="text-gray-900 mt-2 sm:mt-5 font-bold text-sm sm:text-base">
                    Duration: {car.tripDuration}
                  </p>
                  <button className="mt-4 bg-gradient-to-r bg-gradient-to-r from-blue-300 to-blue-400 text-white py-2 px-4 sm:px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto text-sm sm:text-base">
                    <Link to="/Seats">Select Seat</Link>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Trip Details Section */}
        <div className="w-full lg:w-1/3 rounded-lg p-4">
          <br />
          <TripDetailsCard />
        </div>
      </div>
    </>
  );
};

