import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Crysta from "../Images/cryy.jpg";
import Creta from "../Images/creta.jpeg";
import Etios from "../Images/etios.png";
import Gwagon from "../Images/Gwagon.jpg";
import Gurkha from "../Images/Gurkha.jpg";
import Xuv from "../Images/Xuv500.jpg";
import TripDetailsCard from "./TripDetailsCard";

const CarList = () => {
  const initialCarData = [
    {
      id: 1,
      name: "Mercedes G-wagon",
      seats: 6,
      price: 5500,
      image: Gwagon,
      seatsLeft: 1,
      Date: "08/01/2025",
      time: "5:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 2,
      name: "Force Gurkha",
      seats: 6,
      price: 4000,
      image: Gurkha,
      seatsLeft: 2,
      Date: "08/01/2025",
      time: "6:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 3,
      name: "Mahindra Xuv500",
      seats: 6,
      price: 3000,
      seatsLeft: 3,
      image: Xuv,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 4,
      name: "Toyota Innova",
      seats: 6,
      price: 3200,
      seatsLeft: 4,
      image: Crysta,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 5,
      name: "Hyundai Creta",
      seats: 6,
      price: 2500,
      seatsLeft: 5,
      image: Creta,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      id: 6,
      name: "Toyota Etios",
      seats: 6,
      price: 2200,
      seatsLeft: 6,
      image: Etios,
      Date: "08/01/2025",
      time: "7:15 AM",
      tripDuration: "5h 30m",
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    }
    // Additional car objects...
  ];

  const [carData, setCarData] = useState(initialCarData);
  const [sortConfig, setSortConfig] = useState({ key: null, ascending: true });

  const handleSort = (key) => {
    const isAscending =
      sortConfig.key === key ? !sortConfig.ascending : true; // Toggle sort order
    const sortedData = [...carData].sort((a, b) => {
      if (key === "time") {
        const timeA = new Date(`1970-01-01T${a.time}`);
        const timeB = new Date(`1970-01-01T${b.time}`);
        return isAscending ? timeA - timeB : timeB - timeA;
      } else if (key === "price" || key === "seatsLeft") {
        return isAscending ? a[key] - b[key] : b[key] - a[key];
      }
      return 0;
    });

    setCarData(sortedData);
    setSortConfig({ key, ascending: isAscending });
  };


  const location = useLocation();
  // const isActive = ;

  return (
    <>
      <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      <div className="w-full lg:w- mx-auto">
  {/* Tabs */}
  <div className="flex space-x-4 mb-6">
    <button
      className={`px-6 py-3 text-sm sm:text-base font-semibold rounded-lg transition duration-300 ${
        location.pathname === "/sharedcabs"
          ? "bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-md"
          : "bg-gray-200 text-gray-700 hover:bg-gray-300"
      }`}
    >
      <Link to="/sharedcabs">Shared Cars</Link>
    </button>
    <button
      className={`px-6 py-3 text-sm sm:text-base font-semibold rounded-lg transition duration-300 ${
        location.pathname === "/fullcars"
          ? "bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-md"
          : "bg-gray-200 text-gray-700 hover:bg-gray-300"
      }`}
    >
      <Link to="/fullcars">Full Cars</Link>
    </button>
  </div>

  {/* Table Headers */}
  <div className="bg-gray-100 w-full flex text-center rounded-t-lg shadow-sm">
    <div
      onClick={() => handleSort("seatsLeft")}
      className="flex-1 px-6 py-3 text-xs sm:text-sm font-medium text-gray-600 uppercase tracking-wider cursor-pointer hover:bg-gray-200 flex items-center justify-center"
    >
      Seats Left
      <span className="ml-1">
        {sortConfig.key === "seatsLeft"
          ? sortConfig.ascending
            ? "▲"
            : "▼"
          : "⇵"}
      </span>
    </div>
    <div
      onClick={() => handleSort("price")}
      className="flex-1 px-6 py-3 text-xs sm:text-sm font-medium text-gray-600 uppercase tracking-wider cursor-pointer hover:bg-gray-200 flex items-center justify-center"
    >
      Fare
      <span className="ml-1">
        {sortConfig.key === "price"
          ? sortConfig.ascending
            ? "▲"
            : "▼"
          : "⇵"}
      </span>
    </div>
  </div>

  {/* Car Cards */}
  {carData.map((car) => (
    <div
      key={car.id}
      className="bg-white rounded-lg shadow-lg p-6 mb-6 flex flex-col sm:flex-row gap-6"
    >
      {/* Car Image */}
      <img
        src={car.image}
        alt={car.name}
        className="w-full sm:w-1/3 object-cover rounded-lg shadow-md"
      />

      {/* Car Details */}
      <div className="flex-grow flex flex-col justify-between">
        <div>
          <h3 className="text-xl sm:text-2xl font-bold text-gray-800">
            {car.name}
          </h3>
          <p className="text-gray-600 text-sm sm:text-base mt-2">
            {car.seats} Seater
          </p>
          <ul className="text-gray-500 text-sm mt-4 space-y-1">
            {car.features.map((feature, index) => (
              <li key={index} className="flex items-center">
                <span className="w-2 h-2 bg-green-500 rounded-full mr-2"></span>
                {feature}
              </li>
            ))}
          </ul>
        </div>
        <div className="mt-4 sm:mt-0">
          <p className="text-gray-700 text-sm sm:text-base">
            Seats Left: <span className="font-bold">{car.seatsLeft}</span>
          </p>
          <p className="text-xl sm:text-2xl font-bold text-blue-600 mt-2">
            ₹{car.price}/-
          </p>
        </div>
      </div>

      {/* Date, Time, and Duration */}
      <div className="flex flex-col items-center justify-center text-center">
        <h1 className="text-lg sm:text-xl font-bold text-indigo-600">
          {car.Date}
        </h1>
        <h4 className="text-gray-500 text-sm sm:text-base mt-2">
          {car.time}
        </h4>
        <p className="bg-yellow-400 text-gray-900 px-4 py-2 mt-4 rounded-full font-semibold text-sm sm:text-base shadow-md">
          Duration: {car.tripDuration}
        </p>
      </div>

      {/* Select Seat Button */}
      <div className="flex items-end justify-end sm:justify-center">
        <button className="mt-4 sm:mt-0 bg-gradient-to-r from-indigo-500 to-purple-500 text-white py-2 px-6 rounded-full shadow-md hover:from-indigo-600 hover:to-purple-600 focus:outline-none focus:ring-4 focus:ring-purple-200 transition duration-300">
          <Link to="/Seats">Select Seat</Link>
        </button>
      </div>
    </div>
  ))}
</div>

        {/* Trip Details Section */}
        <div className="w-full lg:w-1/3 rounded-lg p-4">
          <br />
          <TripDetailsCard />
        </div>
      </div>
    </>
  );
};
export default CarList;
