import React from "react";
import styled from "styled-components";
import { MdDashboard } from "react-icons/md";
import { BsBoxFill } from "react-icons/bs";
import { FaUserCircle } from "react-icons/fa";
import { Link } from "react-router-dom";
import AiFillHome from "../Images/lion.png";
import { PiShoppingCartFill } from "react-icons/pi";
import { useSelector } from "react-redux";
// import { cartProductSelector } from "../../Api/cartSlice";
import ScrollToTop from "react-scroll-to-top";
import { FaCar } from "react-icons/fa6";
import Hotel from "../Images/resort.png";
import Courier from "../Images/delivery.png"


function Footer() {
  // Replace with the actual cartProducts logic
//   const { cartProducts } = useSelector(cartProductSelector);

  return (
    <FooterWrap>
      {/* <ScrollToTop smooth top="50" /> */}
      <div className="flex justify-around items-center mx-auto py-2 footer md:hidden sm:block fixed bottom-0 left-0 right-0 bg-[#F16022]">
        <Link to="/productGroup" className="flex flex-col items-center justify-center w-16">
          <FaCar className="text-xl text-white font-thin" />
          <span className="text-xs text-white">Cabs</span>
        </Link>

        <Link to="/sparesGroup" className="flex flex-col items-center justify-center w-16">
          <img src={Hotel} className="text-xl text-white h-5" />
          <span className="text-xs text-white">Hotels</span>
        </Link>

        <Link to="/" className="flex flex-col items-center justify-center w-16 mb-5">
          <img className="w-10 h-10 bg-white p-1 rounded-full shadow-xl border border-[#F16022]" src={AiFillHome} alt="Home" />
          <span className="text-xs text-white">Home</span>
        </Link>

        <Link to="/cart" className="relative flex flex-col items-center justify-center w-16">
          <img src={Courier} className="h-6" />
          {/* {cartProducts?.length > 0 && ( */}
            {/* <div className="absolute -top-2 right-2  bg-red-500 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center">
              {cartProducts?.length}
            </div> */}
          {/* )} */}
          <span className="text-xs text-gray-700 font-se">Courier</span>
        </Link>

        <Link to="/login" className="flex flex-col items-center justify-center w-16">
          <FaUserCircle className="text-xl text-white" />
          <span className="text-xs text-white">Tours</span>
        </Link>
      </div>
    </FooterWrap>
  );
}

const FooterWrap = styled.div`
  .footer {
    width: 100%;
    height: 3.0rem;
    position: fixed;
    bottom: 0%;
    left: 0%;
    margin-Top: 0px;
    z-index: 40;
  }

  .dropdown:hover .dropdown-menu {
    display: block;
  }

  .navbtn {
    li {
      list-style: none;
      height: 2px;
      background-color: grey;
      margin: 4px 0;
    }
  }

  .badge,
  .notification {
    position: absolute;
    z-index: auto;
    min-width: 15px;
    height: 15px;
    padding: 0 3px;
    color: #f9f9f9;
    font-size: 10px;
    line-height: 16px;
    white-space: nowrap;
    text-align: center;
    background: #ff4d4f;
    font-weight: bold;
    border-radius: 10px;
    -webkit-box-shadow: 0 0 0 1px #fff;
    box-shadow: 0 0 0 1px #fff;
  }

  .badge {
    transform: translate(14px, -28px);
  }

  .notification {
    transform: translate(11px, -26px);
  }
`;

export default Footer;
