import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import SelectedCarDetails from './SelectedCarDetails';
import TripDetailsCard from '../Listpage/TripDetailsCard';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const [showDummyForm, setShowDummyForm] = useState(true); // State for managing dummy form visibility
  const navigate = useNavigate();

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });

    setShowDummyForm(false); // Hide dummy form on seat selection
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!mainPassenger) {
      setError("There Should be One Main Passenger! Please Fill.");
      return;
    }

    setError(null);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats');
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <>
      <div className="mx-auto flex flex-col md:flex-row gap-8 p-4 w-11/12 md:w-[85%]">
        {/* Dummy Form */}
        {showDummyForm && (
          <div className="w-full md:w-[70%] bg-white shadow-md rounded-lg overflow-hidden p-4">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Passenger Information</h3>
            <form>
              <div className="space-y-4">
                <div>
                  <label htmlFor="dummy-name" className="block text-sm font-medium text-gray-700">
                    Name
                  </label>
                  <input
                    type="text"
                    id="dummy-name"
                    disabled
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                </div>
                <div>
                  <label htmlFor="dummy-phone" className="block text-sm font-medium text-gray-700">
                    Phone
                  </label>
                  <input
                    type="tel"
                    id="dummy-phone"
                    disabled
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                </div>
                <div>
                  <label htmlFor="dummy-email" className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    type="email"
                    id="dummy-email"
                    disabled
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                </div>
                <div className="flex justify-center">
                  <button
                    type="button"
                    disabled
                    className="w-40 py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium bg-gray-200 text-gray-400 cursor-not-allowed"
                  >
                    Book Seats
                  </button>
                </div>
              </div>
            </form>
          </div>
        )}

        {/* Main Form */}
        {!showDummyForm && (
          <div className="w-full md:w-[70%] bg-white shadow-md rounded-lg overflow-hidden">
            <SelectedCarDetails />
            <div className="p-4">
              <div className="w-full md:w-[80%] mx-auto">
                {/* Main Form Content */}
                <form onSubmit={handleSubmit} className="space-y-4">
                  {Object.entries(passengers).map(([seatId, passenger]) => (
                    <div key={seatId} className="space-y-2">
                      {/* Passenger Details */}
                      {/* (Form content from your original code) */}
                    </div>
                  ))}

                  {/* Submit Button */}
                  <div className="flex justify-center">
                    <button
                      type="submit"
                      className={`w-40 py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium ${selectedSeats.length === 0 ? 'text-gray-400 bg-gray-200' : 'text-white bg-blue-600 hover:bg-blue-700 focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'}`}
                      disabled={selectedSeats.length === 0}
                    >
                      Book Seats
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}

        {/* Trip Details */}
        <div className="w-full md:w-[30%] shadow-md rounded-lg overflow-hidden">
          <TripDetailsCard />
          {/* Seat Selection */}
          <div className="p-4">
            <div className="grid grid-rows-3 grid-cols-2 gap-4">
              {seats.map((seat) => (
                <div key={seat.id} className="flex flex-col items-center">
                  <button
                    onClick={() =>
                      !seat.isDisabled && !seat.isBooked && handleSeatClick(seat.id)
                    }
                    className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${seat.isSelected
                        ? 'bg-blue-600 text-white'
                        : seat.isBooked
                          ? 'bg-red-600 text-white'
                          : 'bg-white text-blue-600 border-2 border-blue-600'
                      } ${seat.isDisabled || seat.isBooked
                        ? 'opacity-50 cursor-not-allowed'
                        : 'hover:bg-blue-100'
                      }`}
                    disabled={seat.isDisabled || seat.isBooked}
                  >
                    {seat.id}
                  </button>
                  {!seat.isBooked && !seat.isDisabled && (
                    <span className="mt-2 text-sm text-gray-600">Rs. {seat.price}</span>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default SeatList;
