import React, { useState } from 'react'

function Cab({ name, features, seatsLeft, price }) {
    return (
      <div className="bg-white p-6 rounded-lg shadow-md mb-4">
        <div className="flex justify-between items-start">
          <div>
            <h3 className="text-xl font-bold">{name}</h3>
            <ul className="mt-2">
              {features.map((feature, index) => (
                <li key={index} className="text-sm text-gray-600">{feature}</li>
              ))}
            </ul>
            <p className="mt-2 text-sm text-blue-600">Car Details</p>
            <p className="text-sm text-gray-600">Seats Left: {seatsLeft}</p>
          </div>
          <div className="text-right">
            <p className="text-2xl font-bold">â‚¹{price}/-</p>
            <button className="mt-2 bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50">
              Select Seat
            </button>
            <p className="mt-2 text-sm text-gray-600">6 Seater</p>
          </div>
        </div>
      </div>
    )
  }
  

const CabDetails = () => {
    const [cabType, setCabType] = useState('shared')

  const cabs = [
    {
      name: 'Toyota Innova',
      features: [
        'Air Conditioned Luxury SUV',
        'Comfortable & Spacious Seating',
        'Female Passenger Safety Standards'
      ],
      seatsLeft: 5,
      price: 1200
    },
    {
      name: 'Maruti Suzuki Ertiga',
      features: [
        'Fuel Efficient MPV',
        'Comfortable Seating for 7',
        '24/7 Roadside Assistance'
      ],
      seatsLeft: 3,
      price: 1000
    },
    {
      name: 'Mahindra XUV500',
      features: [
        'Powerful SUV for Hill Drives',
        'Advanced Safety Features',
        'Panoramic Sunroof'
      ],
      seatsLeft: 4,
      price: 1500
    }
  ]
  return (
    <div className="mt-8">
    <div className="flex justify-between items-center mb-4">
      <h2 className="text-2xl font-bold">Available Cabs</h2>
      <div className="flex space-x-2">
        <button
          className={`py-2 px-4 rounded-md ${cabType === 'shared' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
          onClick={() => setCabType('shared')}
        >
          Shared Cars
        </button>
        <button
          className={`py-2 px-4 rounded-md ${cabType === 'full' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
          onClick={() => setCabType('full')}
        >
          Full Cars
        </button>
      </div>
    </div>
    {cabs.map((cab, index) => (
      <Cab key={index} {...cab} />
    ))}
  </div>
  )
}

export default CabDetails
