import React from 'react';
import BookingForm from '../Home/bookingForm';
import Crysta from '../Images/cryy.jpg';
import { Link } from 'react-router-dom';
import TripDetailsCard from './TripDetailsCard';

const CarList = () => {
  const carData = [
    {
      id: 1,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date:"08/01/2025",
      time:"5.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 2,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date:"08/01/2025",
      time:"6.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 3,
      name: "Toyota Innova",
      seats: 6,
      price: 1000,
      seatsLeft: 5,
      image: Crysta,
      Date:"08/01/2025",
      time:"7.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
      
    },
    {
      id: 4,
      name: "Toyota Innova",
      seats: 6,
      price: 1100,
      image: Crysta,
      seatsLeft: 1,
      Date:"08/01/2025",
      time:"8.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 5,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date:"08/01/2025",
      time:"9.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 6,
      name: "Toyota Innova",
      seats: 6,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date:"08/01/2025",
      time:"10.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    
  ];

  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      {/* Trip Details Section */}
      <tr className="bg-gray-50">
                      {/* <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Car
                      </th> */}
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          From
                          
                        </div>
                      </th>
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          To
                          
                        </div>
                      </th>
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          Date

                        </div>
                      </th>
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          Time
                          
                        </div>
                      </th>
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          Fare

                        </div>
                      </th>
                      <th  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100">
                        <div className="flex items-center">
                          Status
                          
                        </div>
                      </th>
                    </tr>
      {/* Car List Section */}
      <div className="w-full lg:w-2/3">
        {/* Tabs */}
        {/* <div className="flex space-x-4 mb-4">
          <button className="px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
            Shared Cars
          </button>
          <button className="px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
            Full Cars
          </button>
        </div> */}

        {/* Car Cards */}
        {carData.map((car) => (
  <div
    key={car.id}
    className="bg-white rounded-lg shadow-md p-4 mb-4 flex flex-col lg:flex-row gap-4"
  >
    <img
      src={car.image} // Replace with actual car image URL
      alt={car.name}
      className="w-full lg:w-1/3 object-cover rounded-lg"
    />
    <div className="flex flex-col lg:flex-row justify-between w-full">
      <div className="flex-grow">
        <h3 className="text-xl font-bold">{car.name}</h3>
        <p className="text-gray-900 mb-2">{car.seats} Seater</p>
        <ul className="text-gray-700 text-sm mb-4">
          {car.features.map((feature, index) => (
            <li key={index}>• {feature}</li>
          ))}
        </ul>
      </div>
      {/* Date and Time in the center */}
      <div className="flex flex-col items-center justify-center text-center">
        <h1 className="text-lg font-semibold">{car.Date}</h1>
        <h4 className="text-gray-600">{car.time}</h4>
      </div>
      <div className="flex flex-col items-end justify-between text-right">
        <p className="text-gray-900 mt-5 font-bold">Seats Left: {car.seatsLeft}</p>
        <p className="text-2xl sm:text-3xl font-bold text-blue-600">₹{car.price}/-</p>
        <button className="mt-4 bg-gradient-to-r from-green-400 to-green-500 text-white py-2 px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto">
          <Link to="/Seats">Select Seat</Link>
        </button>
      </div>
    </div>
  </div>
))}


      </div>

      <div className="w-full lg:w-1/4  rounded-lg p-4 ">
        {/* <h2 className="text-xl font-bold mb-4">Trip Details</h2> */}
        <br />
        {/* <BookingForm className="bg-blue-500 mt-0" buttonLabel="Modify"/> */}
        <TripDetailsCard />
      </div>
    </div>
  );
};

export default CarList;
