import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BookingForm from '../Home/bookingForm';
import SelectedCarDetails from './SelectedCarDetails';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);
  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [razorpayScriptLoaded, setRazorpayScriptLoaded] = useState(false);

  // Load Razorpay script dynamically
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.async = true;
    script.onload = () => setRazorpayScriptLoaded(true);
    script.onerror = () => alert('Failed to load Razorpay script');
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handlePayment = async () => {
    if (!razorpayScriptLoaded) {
      alert('Razorpay script is not loaded!');
      return;
    }

    const response = await fetch('http://localhost:5000/api/payments/create-order', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ totalAmount: totalAmount }),
    });

    const order = await response.json();

    if (!order || !order.id) {
      alert('Failed to create order');
      return;
    }

    const options = {
      key: process.env.REACT_APP_RAZORPAY_KEY_ID, // Ensure this environment variable is correct
      amount: totalAmount * 100, // Convert to paise
      currency: 'INR',
      name: 'Your Company',
      description: 'Booking Details',
      order_id: order.id,
      handler: async (response) => {
        const paymentDetails = {
          paymentId: response.razorpay_payment_id,
          orderId: response.razorpay_order_id,
          signature: response.razorpay_signature,
          paymentDetails: {
            method: 'Razorpay',
            status: 'success',
            amount: totalAmount,
            mainPassenger: passengers[mainPassenger],
            seatsBooked: selectedSeats,
            tripDetails: {
              fromLocation: 'Delhi',
              toLocation: 'Mumbai',
              pickupLocation: 'Rajiv Chowk',
              dropLocation: 'Andheri West',
              dateOfTrip: new Date(),
            },
          },
        };

        const captureResponse = await fetch('http://localhost:5000/api/payments/capture-payment', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(paymentDetails),
        });

        const result = await captureResponse.json();
        if (result.success) {
          alert('Payment successful');
        } else {
          alert('Payment failed');
        }
      },
      prefill: {
        name: 'Customer Name',
        email: 'customer@example.com',
        contact: '1234567890',
      },
      theme: {
        color: '#F37254',
      },
    };

    const rzp1 = new window.Razorpay(options); // Razorpay constructor
    rzp1.open();
  };

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!mainPassenger) {
      setError('There should be one main passenger! Please fill.');
      return;
    }

    setError(null); // Clear any previous error
    setShowModal(true); // Show the modal on form submission
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats'); // Navigate to the Seats page
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <div className="flex flex-col md:flex-row gap-8 p-4">
      {/* Booking Form */}
      <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
        <SelectedCarDetails />
        <div className="bg-gray-100 px-4 py-2">
          <h2 className="text-lg font-semibold">Booking Information</h2>
        </div>
        <BookingForm className="mt-0" buttonLabel="Modify" />
        <div className="p-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            {Object.entries(passengers).map(([seatId, passenger]) => (
              <div key={seatId} className="space-y-2">
                <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                  Passenger Name for Seat {seatId}
                </label>
                <input
                  type="text"
                  id={`name-${seatId}`}
                  value={passenger.name}
                  onChange={(e) =>
                    setPassengers((prev) => ({
                      ...prev,
                      [seatId]: { ...prev[seatId], name: e.target.value },
                    }))
                  }
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                />
                <label className="inline-flex items-center mt-2">
                  <input
                    type="checkbox"
                    checked={mainPassenger === seatId}
                    onChange={() => handleMainPassengerChange(seatId)}
                    className="form-checkbox text-blue-600"
                  />
                  <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                </label>
                {mainPassenger === seatId && (
                  <div className="mt-2">
                    <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                      Email
                    </label>
                    <input
                      type="email"
                      id={`email-${seatId}`}
                      value={passenger.email}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], email: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                    />
                    <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700 mt-2">
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      id={`phone-${seatId}`}
                      value={passenger.phone}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], phone: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                    />
                  </div>
                )}
              </div>
            ))}

            {/* Error Message */}
            {error && (
              <div className="text-red-600 text-sm font-medium">{error}</div>
            )}

            {/* No Seats Selected Message */}
            {selectedSeats.length === 0 && (
              <div className="bg-yellow-100 text-yellow-700 border border-yellow-400 rounded-md p-2 text-center">
                Please select the seats for the trip.
              </div>
            )}

            {selectedSeats.length > 0 && (
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Book Seats
              </button>
            )}
          </form>
        </div>
      </div>

      {/* Seat Selection */}
      <div className="flex flex-col md:w-1/3 p-6 bg-white shadow-md rounded-lg space-y-4">
        <h2 className="text-lg font-semibold">Seat Selection</h2>
        <div className="grid grid-cols-3 gap-4">
          {seats.map((seat) => (
            <button
              key={seat.id}
              disabled={seat.isDisabled}
              onClick={() => handleSeatClick(seat.id)}
              className={`p-2 text-center rounded-md ${
                seat.isSelected
                  ? 'bg-blue-500 text-white'
                  : seat.isBooked
                  ? 'bg-gray-500 text-gray-300 cursor-not-allowed'
                  : 'bg-gray-200'
              }`}
            >
              {seat.id}
            </button>
          ))}
        </div>
      </div>

      {/* Payment Modal */}
      {showModal && (
        <div
          className="fixed inset-0 flex justify-center items-center bg-gray-700 bg-opacity-50 z-50"
          onClick={closeModal}
        >
          <div
            className="bg-white p-6 rounded-lg shadow-lg"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-lg font-semibold mb-4">Proceed to Payment</h2>
            <p className="mb-4">Total Amount: ₹{totalAmount}</p>
            <button
              onClick={handlePayment}
              className="px-6 py-2 bg-green-600 text-white rounded-md"
            >
              Pay Now
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatList;
