import { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate

export default function BookingForm({ className, buttonLabel = "Search for Cabs" }) {
  const [formData, setFormData] = useState({
    from: "",
    to: "",
    pickupStreet: "",
    dropStreet: "",
    date: "",
    seats: "", // Add seats to the form data
  });

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [error, setError] = useState(""); // For validation errors
  const navigate = useNavigate(); // Initialize useNavigate

  const locations = ["Bangalore", "Electronic City", "Coorg", "Mysore"];
  const streets = ["Indiranagar", "Ullal", "Tin Factory"]; // Example streets for each location

  // Example: Initialize passengers based on selected seats
  // This should be handled appropriately based on your actual logic
  useEffect(() => {
    const seatCount = parseInt(formData.seats, 10);
    if (seatCount > 0) {
      const newPassengers = {};
      for (let i = 1; i <= seatCount; i++) {
        newPassengers[i] = { name: "", email: "", phone: "" };
      }
      setPassengers(newPassengers);
    } else {
      setPassengers({});
    }
  }, [formData.seats]);

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger(seatId);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation logic...
    // (Assuming you've already handled validation as per previous steps)

    // Example navigation after successful submission
    navigate("/cabdetails");

    console.log("Form submitted:", formData, passengers);
  };

  const today = new Date();
  const currentDate = today.toISOString().split("T")[0];

  // Determine the number of selected seats
  const passengerCount = Object.keys(passengers).length;

  return (
    <div className="w-full md:w-[90%] lg:w-[80%] bg-white shadow-md rounded-lg overflow-hidden mx-auto">
      <SelectedCarDetails />
      <div className="p-6">
        <div className="w-full mx-auto mt-10">
          <form onSubmit={handleSubmit} className="bg-gray-50 p-8 shadow-lg rounded-lg">
            <h2 className="text-2xl font-semibold text-center text-gray-900 mb-8">Passenger Details</h2>

            {/* Passenger Details Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {Object.entries(passengers).map(([seatId, passenger]) => (
                <div
                  key={seatId}
                  className={`bg-white p-6 rounded-md shadow-sm border space-y-4 flex flex-col ${
                    passengerCount === 1 ? "col-span-2" : ""
                  }`}
                >
                  <h3 className="text-lg font-medium text-gray-800">Passenger for Seat {seatId}</h3>

                  {/* Passenger Name */}
                  <div>
                    <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                      Name
                    </label>
                    <input
                      type="text"
                      id={`name-${seatId}`}
                      value={passenger.name}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], name: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                    />
                  </div>

                  {/* Main Passenger Checkbox */}
                  <div>
                    <label className="flex items-center">
                      <input
                        type="checkbox"
                        checked={mainPassenger === seatId}
                        onChange={() => handleMainPassengerChange(seatId)}
                        className="form-checkbox h-5 w-5 text-blue-600"
                      />
                      <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                    </label>
                  </div>

                  {mainPassenger === seatId && (
                    <div className="space-y-4">
                      {/* Email and Phone in Flex */}
                      <div className="flex flex-col md:flex-row gap-4">
                        {/* Email */}
                        <div className="flex-1">
                          <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                            Email
                          </label>
                          <input
                            type="email"
                            id={`email-${seatId}`}
                            value={passenger.email}
                            onChange={(e) =>
                              setPassengers((prev) => ({
                                ...prev,
                                [seatId]: { ...prev[seatId], email: e.target.value },
                              }))
                            }
                            required
                            className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                          />
                        </div>

                        {/* Phone */}
                        <div className="flex-1">
                          <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700">
                            Phone Number
                          </label>
                          <input
                            type="tel"
                            id={`phone-${seatId}`}
                            value={passenger.phone}
                            onChange={(e) =>
                              setPassengers((prev) => ({
                                ...prev,
                                [seatId]: { ...prev[seatId], phone: e.target.value },
                              }))
                            }
                            required
                            className="mt-1 w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 px-3 py-2"
                          />
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Error Message */}
            {error && <div className="text-red-500 text-center mt-4">{error}</div>}

            {/* No Seats Selected Message */}
            {passengerCount === 0 && (
              <div className="bg-yellow-100 border-l-4 border-yellow-400 text-yellow-700 p-4 mt-6">
                <p className="font-medium">No seats selected!</p>
                <p>Please select seats for the trip before proceeding.</p>
              </div>
            )}

            {/* Submit Button */}
            <div className="text-center mt-8">
              <button
                type="submit"
                disabled={passengerCount === 0}
                className={`px-6 py-3 text-sm font-medium rounded-md shadow ${
                  passengerCount === 0
                    ? "bg-gray-200 text-gray-500 cursor-not-allowed"
                    : "bg-blue-600 text-white hover:bg-blue-700 focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                }`}
              >
                Book Seats
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
