import React, { useState } from "react";
import { Link } from "react-router-dom";

const CarList = () => {
  const [filters, setFilters] = useState({
    time: "",
    price: "",
    seats: "",
    seatsLeft: "",
  });

  const carData = [
    {
      id: 1,
      name: "Toyota Innova",
      seats: 9,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date: "08/01/2025",
      time: "5.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    // ... other car data
  ];

  // Apply filters to car data
  const filteredCars = carData.filter((car) => {
    return (
      (filters.time ? car.time.includes(filters.time) : true) &&
      (filters.price ? car.price <= Number(filters.price) : true) &&
      (filters.seats ? car.seats >= Number(filters.seats) : true) &&
      (filters.seatsLeft ? car.seatsLeft >= Number(filters.seatsLeft) : true)
    );
  });

  // Handle filter input changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      {/* Filter Section */}
      <div className="w-full lg:w-1/4 bg-white p-4 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4">Filters</h2>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Time</label>
          <input
            type="text"
            name="time"
            placeholder="e.g., 5.15 AM"
            value={filters.time}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Max Price</label>
          <input
            type="number"
            name="price"
            placeholder="e.g., 1200"
            value={filters.price}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Min Seats</label>
          <input
            type="number"
            name="seats"
            placeholder="e.g., 6"
            value={filters.seats}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-2">Min Seats Left</label>
          <input
            type="number"
            name="seatsLeft"
            placeholder="e.g., 5"
            value={filters.seatsLeft}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          />
        </div>
      </div>

      {/* Car List Section */}
      <div className="w-full lg:w-3/4">
        {filteredCars.map((car) => (
          <div
            key={car.id}
            className="bg-white rounded-lg shadow-md p-4 mb-4 flex flex-col lg:flex-row gap-4"
          >
            <img
              src={car.image}
              alt={car.name}
              className="w-full lg:w-1/3 object-cover rounded-lg"
            />
            <div className="flex flex-col lg:flex-row justify-between w-full">
              <div className="flex-grow">
                <h3 className="text-xl font-bold">{car.name}</h3>
                <p className="text-gray-900 mb-2">{car.seats} Seater</p>
                <ul className="text-gray-700 text-sm mb-4">
                  {car.features.map((feature, index) => (
                    <li key={index}>• {feature}</li>
                  ))}
                </ul>
              </div>
              <div className="flex flex-col items-center justify-center text-center">
                <h1 className="text-lg font-semibold">{car.Date}</h1>
                <h4 className="text-gray-600">{car.time}</h4>
              </div>
              <div className="flex flex-col items-end justify-between text-right">
                <p className="text-gray-900 mt-5 font-bold">
                  Seats Left: {car.seatsLeft}
                </p>
                <p className="text-2xl sm:text-3xl font-bold text-blue-600">
                  ₹{car.price}/-
                </p>
                <button className="mt-4 bg-gradient-to-r from-green-400 to-green-500 text-white py-2 px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto">
                  <Link to="/Seats">Select Seat</Link>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarList;
