import React from "react";
import styled from "styled-components";
import { MdDashboard } from "react-icons/md";
import { BsBoxFill } from "react-icons/bs";
import { FaUserCircle } from "react-icons/fa";
import { Link } from "react-router-dom";
import AiFillHome from "../Images/lion.png";
import TGH from "../Images/tgh.png";
import { PiShoppingCartFill } from "react-icons/pi";
import { useSelector } from "react-redux";
// import { cartProductSelector } from "../../Api/cartSlice";
import ScrollToTop from "react-scroll-to-top";
import { FaCar } from "react-icons/fa6";
import Hotel from "../Images/resorts.png";
import Courier from "../Images/delivery.png";
import Tours from "../Images/Tours.png";
import Cabs from "../Images/transport.png"


function Footer() {
  // Replace with the actual cartProducts logic
//   const { cartProducts } = useSelector(cartProductSelector);

  return (
    <FooterWrap>
      {/* <ScrollToTop smooth top="50" /> */}
      <div className=" top-border flex justify-around items-center mx-auto py-2 footer md:hidden sm:block fixed bottom-0 left-0 right-0 top-border z-auto">
        <Link to="/" className="flex flex-col items-center justify-center w-16 mt-1">
          <img src={Cabs} className="h-8 w-8" />
          <span className="text-xs text-gray-700 font-bold">Cabs</span>
        </Link>

        <Link to="/sparesGroup" className="flex flex-col items-center justify-center w-16">
          <img src={Hotel} className="h-8 w-8 mt-1" />
          <span className="text-xs text-gray-700 font-bold">Hotels</span>
        </Link>

        <Link to="/productGroup" className="flex flex-col items-center justify-center w-16 mb-5">
          <img className="w-11 h-11 bg-white p-1 rounded-full shadow-xl border-t border-[#696969]" src={TGH} alt="Home" />
          <span className="text-xs text-gray-700 font-bold">Home</span>
        </Link>

        <Link to="/cart" className="relative flex flex-col items-center justify-center w-16">
          <img src={Courier} className="h-8 w-8 mt-1" />
          {/* {cartProducts?.length > 0 && ( */}
            {/* <div className="absolute -top-2 right-2  bg-red-500 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center">
              {cartProducts?.length}
            </div> */}
          {/* )} */}
          <span className="text-xs text-gray-700 font-bold">Courier</span>
        </Link>

        <Link to="/login" className="flex flex-col items-center justify-center w-16">
          <img src={Tours} className="h-8 w-8 mt-1" />
          <span className="text-xs text-gray-700 font-bold">Tours</span>
        </Link>
      </div>
    </FooterWrap>
  );
}

