const FooterWrap = styled.div`
  .footer {
    width: 100%;
    height: 3.0rem;
    position: fixed;
    bottom: 2px;
    left: 0%;
    margin-top: 1px;
    z-index: 40;
    border-top: 1px solid #696969; 
  }

