import React, { useState } from 'react';

function Cab({ name, features, seatsLeft, price }) {
  return (
    <div className="bg-gradient-to-r from-blue-50 via-white to-blue-100 p-6 rounded-lg shadow-lg mb-6 transition transform hover:scale-105 sm:flex sm:justify-between sm:items-start">
      <div className="sm:w-3/4">
        <h3 className="text-xl sm:text-2xl font-extrabold text-gray-800">{name}</h3>
        <ul className="mt-3 space-y-1">
          {features.map((feature, index) => (
            <li
              key={index}
              className="text-sm sm:text-base text-gray-700 flex items-center"
            >
              <span className="mr-2 text-blue-500">✔</span>
              {feature}
            </li>
          ))}
        </ul>
        <p className="mt-4 text-sm sm:text-base text-blue-600 font-semibold cursor-pointer hover:underline">
          View Car Details
        </p>
        <p className="text-sm sm:text-base text-gray-700 mt-2">Seats Left: {seatsLeft}</p>
      </div>
      <div className="text-right sm:w-1/4 sm:ml-4">
        <p className="text-2xl sm:text-3xl font-bold text-blue-600">₹{price}/-</p>
        <button className="mt-4 bg-gradient-to-r from-green-400 to-green-500 text-white py-2 px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto">
          Select Seat
        </button>
        <p className="mt-3 text-sm sm:text-base text-gray-600">6 Seater</p>
      </div>
    </div>
  );
}

const CabDetails = () => {
  const [cabType, setCabType] = useState('shared');

  const cabs = [
    {
      name: 'Toyota Innova',
      features: [
        'Air Conditioned Luxury SUV',
        'Comfortable & Spacious Seating',
        'Female Passenger Safety Standards',
      ],
      seatsLeft: 5,
      price: 1200,
    },
    {
      name: 'Maruti Suzuki Ertiga',
      features: [
        'Fuel Efficient MPV',
        'Comfortable Seating for 7',
        '24/7 Roadside Assistance',
      ],
      seatsLeft: 3,
      price: 1000,
    },
    {
      name: 'Mahindra XUV500',
      features: [
        'Powerful SUV for Hill Drives',
        'Advanced Safety Features',
        'Panoramic Sunroof',
      ],
      seatsLeft: 4,
      price: 1500,
    },
  ];

  return (
    <div className="mt-10 mx-auto max-w-6xl px-4">
      <div className="flex flex-col sm:flex-row justify-between items-center mb-6">
        <h2 className="text-2xl sm:text-3xl font-bold text-gray-800 mb-4 sm:mb-0">
          Available Cabs
        </h2>
        <div className="flex space-x-4">
          <button
            className={`py-2 px-6 rounded-full font-medium text-sm sm:text-base transition ${
              cabType === 'shared'
                ? 'bg-blue-600 text-white shadow-lg'
                : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
            }`}
            onClick={() => setCabType('shared')}
          >
            Shared Cars
          </button>
          <button
            className={`py-2 px-6 rounded-full font-medium text-sm sm:text-base transition ${
              cabType === 'full'
                ? 'bg-blue-600 text-white shadow-lg'
                : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
            }`}
            onClick={() => setCabType('full')}
          >
            Full Cars
          </button>
        </div>
      </div>
      {cabs.map((cab, index) => (
        <Cab key={index} {...cab} />
      ))}
    </div>
  );
};

export default CabDetails;
