import Crysta from "../Images/cryy.jpg";

const CabDetails = () => {
  return (
    // <div className="mt-10 mx-auto max-w-6xl px-4">
    //   <h2 className="text-2xl font-bold text-gray-800 mb-6">Toyota Innova</h2>
      
    //   <div className="flex flex-col sm:flex-row bg-blue-50 p-6 rounded-lg shadow-md">
