import React, { useState } from "react";
import { Link } from "react-router-dom";

const CarList = () => {
  const [filters, setFilters] = useState({
    time: "all",
    price: "all",
    seats: "all",
    seatsLeft: "all",
  });

  const carData = [
    {
      id: 1,
      name: "Toyota Innova",
      seats: 9,
      price: 1200,
      image: Crysta,
      seatsLeft: 5,
      Date: "08/01/2025",
      time: "5.15 AM",
      features: [
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    // ... other car data
  ];

  // Filter logic
  const filteredCars = carData.filter((car) => {
    // Time filter
    const timeFilter =
      filters.time === "all" || car.time.includes(filters.time);

    // Price filter
    const priceFilter =
      filters.price === "all" ||
      (filters.price === "below1000" && car.price < 1000) ||
      (filters.price === "1000-1200" && car.price >= 1000 && car.price <= 1200) ||
      (filters.price === "above1200" && car.price > 1200);

    // Seats filter
    const seatsFilter =
      filters.seats === "all" ||
      (filters.seats === "6" && car.seats === 6) ||
      (filters.seats === "7-9" && car.seats >= 7 && car.seats <= 9);

    // Seats left filter
    const seatsLeftFilter =
      filters.seatsLeft === "all" ||
      (filters.seatsLeft === "1-3" && car.seatsLeft >= 1 && car.seatsLeft <= 3) ||
      (filters.seatsLeft === "4-5" && car.seatsLeft >= 4 && car.seatsLeft <= 5);

    return timeFilter && priceFilter && seatsFilter && seatsLeftFilter;
  });

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      {/* Filter Section */}
      <div className="w-full lg:w-1/4 bg-white p-4 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4">Filters</h2>

        {/* Time Filter */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Time</label>
          <select
            name="time"
            value={filters.time}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          >
            <option value="all">All Times</option>
            <option value="5.15 AM">5.15 AM</option>
            <option value="6.15 AM">6.15 AM</option>
            <option value="7.15 AM">7.15 AM</option>
          </select>
        </div>

        {/* Price Filter */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Price</label>
          <select
            name="price"
            value={filters.price}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          >
            <option value="all">All Prices</option>
            <option value="below1000">Below ₹1000</option>
            <option value="1000-1200">₹1000 - ₹1200</option>
            <option value="above1200">Above ₹1200</option>
          </select>
        </div>

        {/* Seats Filter */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Seats</label>
          <select
            name="seats"
            value={filters.seats}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          >
            <option value="all">All Seat Options</option>
            <option value="6">6 Seater</option>
            <option value="7-9">7-9 Seater</option>
          </select>
        </div>

        {/* Seats Left Filter */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Seats Left</label>
          <select
            name="seatsLeft"
            value={filters.seatsLeft}
            onChange={handleFilterChange}
            className="w-full px-3 py-2 border rounded-md"
          >
            <option value="all">All Seats Left</option>
            <option value="1-3">1-3 Seats Left</option>
            <option value="4-5">4-5 Seats Left</option>
          </select>
        </div>
      </div>

      {/* Car List Section */}
      <div className="w-full lg:w-3/4">
        {filteredCars.map((car) => (
          <div
            key={car.id}
            className="bg-white rounded-lg shadow-md p-4 mb-4 flex flex-col lg:flex-row gap-4"
          >
            <img
              src={car.image}
              alt={car.name}
              className="w-full lg:w-1/3 object-cover rounded-lg"
            />
            <div className="flex flex-col lg:flex-row justify-between w-full">
              <div className="flex-grow">
                <h3 className="text-xl font-bold">{car.name}</h3>
                <p className="text-gray-900 mb-2">{car.seats} Seater</p>
                <ul className="text-gray-700 text-sm mb-4">
                  {car.features.map((feature, index) => (
                    <li key={index}>• {feature}</li>
                  ))}
                </ul>
              </div>
              <div className="flex flex-col items-center justify-center text-center">
                <h1 className="text-lg font-semibold">{car.Date}</h1>
                <h4 className="text-gray-600">{car.time}</h4>
              </div>
              <div className="flex flex-col items-end justify-between text-right">
                <p className="text-gray-900 mt-5 font-bold">
                  Seats Left: {car.seatsLeft}
                </p>
                <p className="text-2xl sm:text-3xl font-bold text-blue-600">
                  ₹{car.price}/-
                </p>
                <button className="mt-4 bg-gradient-to-r from-green-400 to-green-500 text-white py-2 px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto">
                  <Link to="/Seats">Select Seat</Link>
                </button>
              </div>
            </div>
          </div>
        ))}

        {filteredCars.length === 0 && (
          <p className="text-center text-gray-600">No cars match the filters.</p>
        )}
      </div>
    </div>
  );
};

export default CarList;
