import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SelectedCarDetails from './SelectedCarDetails';
import TripDetailsCard from '../Listpage/TripDetailsCard';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);
  
  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Payment Handling
  const handlePayment = async () => {
    const response = await fetch('/api/payment/create-order', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ amount: totalAmount }), // Pass the total amount here
    });
    const data = await response.json();
    const orderId = data.orderId;
  
    const options = {
      key: "rzp_test_vqDlqzfsth2zPW",
      amount: totalAmount * 100, // Amount in paise
      currency: 'INR',
      name: 'Your Company',
      description: 'Payment for booking',
      order_id: orderId,
      handler: async function (response) {
        const verificationResponse = await fetch('/api/payment/verify-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            paymentId: response.razorpay_payment_id,
            orderId: response.razorpay_order_id,
            signature: response.razorpay_signature,
          }),
        });
        const verificationData = await verificationResponse.json();
        if (verificationData.message === 'Payment verified successfully') {
          // Handle success, maybe redirect or show a success message
        }
      },
      prefill: {
        name: 'Passenger Name',
        email: 'passenger@example.com',
        contact: '9999999999',
      },
      theme: {
        color: '#3399cc',
      },
    };
  
    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) delete updatedPassengers[id];
      else updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!mainPassenger) {
      setError('There Should be an One Main Passenger ! Please Fill.');
      return;
    }
    setError(null);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats');
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <>
    <div className="flex justify-center p-4 w-full">
      {/* Main Container */}
      <div className="flex flex-col md:flex-row gap-8 w-[85%]">
        {/* First Div */}
        <div className="flex-[7] bg-white shadow-md rounded-lg overflow-hidden">
          <SelectedCarDetails />
        </div>

        {/* Second Div */}
        <div className="flex-[3] shadow-md rounded-lg overflow-hidden">
          <TripDetailsCard />
          <div className="p-4">
            <form onSubmit={handleSubmit} className="space-y-4">
              {Object.entries(passengers).map(([seatId, passenger]) => (
                <div key={seatId} className="space-y-2">
                  <label
                    htmlFor={`name-${seatId}`}
                    className="block text-sm font-medium text-gray-700"
                  >
                    Passenger Name for Seat {seatId}
                  </label>
                  <input
                    type="text"
                    id={`name-${seatId}`}
                    value={passenger.name}
                    onChange={(e) =>
                      setPassengers((prev) => ({
                        ...prev,
                        [seatId]: { ...prev[seatId], name: e.target.value },
                      }))
                    }
                    required
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                  <label className="inline-flex items-center mt-2">
                    <input
                      type="checkbox"
                      checked={mainPassenger === seatId}
                      onChange={() => handleMainPassengerChange(seatId)}
                      className="form-checkbox text-blue-600"
                    />
                    <span className="ml-2 text-sm text-gray-700">
                      Set as Main Passenger
                    </span>
                  </label>
                  {mainPassenger === seatId && (
                    <div className="mt-2">
                      <label
                        htmlFor={`email-${seatId}`}
                        className="block text-sm font-medium text-gray-700"
                      >
                        Email
                      </label>
                      <input
                        type="email"
                        id={`email-${seatId}`}
                        value={passenger.email}
                        onChange={(e) =>
                          setPassengers((prev) => ({
                            ...prev,
                            [seatId]: {
                              ...prev[seatId],
                              email: e.target.value,
                            },
                          }))
                        }
                        required
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                      />
                      <label
                        htmlFor={`phone-${seatId}`}
                        className="block text-sm font-medium text-gray-700 mt-2"
                      >
                        Phone Number
                      </label>
                      <input
                        type="tel"
                        id={`phone-${seatId}`}
                        value={passenger.phone}
                        onChange={(e) =>
                          setPassengers((prev) => ({
                            ...prev,
                            [seatId]: {
                              ...prev[seatId],
                              phone: e.target.value,
                            },
                          }))
                        }
                        required
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                      />
                    </div>
                  )}
                </div>
              ))}

              {/* Error Message */}
              {error && (
                <div className="text-red-600 text-sm font-medium">{error}</div>
              )}

              {/* No Seats Selected Message */}
              {selectedSeats.length === 0 && (
                <div className="bg-yellow-100 text-yellow-700 border border-yellow-400 rounded-md p-2 text-center">
                  Please select the seats for the trip.
                </div>
              )}

              {selectedSeats.length > 0 && (
                <button
                  type="submit"
                  className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Book Seats
                </button>
              )}
            </form>
          </div>
          <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
            <div className="bg-gray-100 px-4 py-2">
              <h2 className="text-lg font-semibold">Select Seats</h2>
            </div>
            <div className="p-4">
              <div className="grid grid-rows-3 grid-cols-2 gap-4">
                {seats.map((seat) => (
                  <div key={seat.id} className="flex flex-col items-center">
                    <button
                      onClick={() =>
                        !seat.isDisabled &&
                        !seat.isBooked &&
                        handleSeatClick(seat.id)
                      }
                      className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${
                        seat.isSelected
                          ? 'bg-blue-600 text-white'
                          : seat.isBooked
                          ? 'bg-red-600 text-white'
                          : 'bg-white text-blue-600 border-2 border-blue-600'
                      } ${
                        seat.isDisabled || seat.isBooked
                          ? 'opacity-50 cursor-not-allowed'
                          : 'hover:bg-blue-100'
                      }`}
                      disabled={seat.isDisabled || seat.isBooked}
                    >
                      {seat.id}
                    </button>
                    {!seat.isBooked && !seat.isDisabled && (
                      <span className="mt-2 text-sm text-gray-600">
                        Rs. {seat.price}
                      </span>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white w-11/12 max-w-3xl rounded-xl shadow-2xl p-8 relative animate-fade-in">
            {/* Close Button */}
            <button
              onClick={closeModal}
              className="absolute top-3 right-3 text-gray-700 hover:text-gray-900 bg-gray-200 rounded-full p-2 shadow transition hover:scale-110"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                className="h-6 w-6"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
            <h3 className="text-2xl font-semibold text-center mb-4">Booking Summary</h3>
            <div className="overflow-y-auto max-h-80">
              <div>
                {selectedSeats.map((seat) => (
                  <div key={seat.id} className="flex justify-between py-2">
                    <span className="text-lg font-medium">{seat.id}</span>
                    <span className="text-lg">{seat.price}</span>
                  </div>
                ))}
                <div className="flex justify-between pt-2 mt-2 border-t">
                  <span className="text-lg font-semibold">Total</span>
                  <span className="text-lg font-semibold">{totalAmount}</span>
                </div>
              </div>
            </div>
            <div className="mt-4">
              <button
                onClick={handlePayment}
                className="w-full py-2 px-4 bg-blue-600 text-white text-lg rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Proceed to Payment
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
    </>
  );
};

export default SeatList;
