import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import SelectedCarDetails from './SelectedCarDetails';
import TripDetailsCard from '../Listpage/TripDetailsCard';
import axios from 'axios';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);
  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handlePayment = async () => {
    try {
      const { data } = await axios.post("http://localhost:5000/api/payment/create-order", {
        amount: totalAmount * 100, // Amount in paise
      });

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: data.amount,
        currency: data.currency,
        name: "The CabStatioN",
        description: "Test Transaction",
        order_id: data.id,
        handler: async function (response) {
          alert(`Payment Successful! Payment ID: ${response.razorpay_payment_id}`);
        },
        prefill: {
          name: "",
          email: "",
          contact: "",
        },
        theme: {
          color: "#3399cc",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.error("Error in Razorpay payment:", error);
    }
  };

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!mainPassenger) {
      setError("There Should be an One Main Passenger! Please Fill.");
      return;
    }

    setError(null);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats');
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <>
      <div className="mx-auto flex flex-col md:flex-row gap-8 p-4 w-11/12 md:w-[85%]">
        <div className="w-full md:w-[70%] bg-white shadow-md rounded-lg overflow-hidden">
          <SelectedCarDetails />
          <div className="p-4">
            <div className="w-full md:w-[80%] lg:w-[60%] mx-auto mt-10">
              <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 shadow-md rounded-md">
                <h2 className="text-2xl font-semibold text-gray-800 text-center">Passenger Details</h2>

                {Object.entries(passengers).map(([seatId, passenger]) => (
                  <div key={seatId} className="space-y-4">
                    <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                      Passenger Name for Seat {seatId}
                    </label>
                    <input
                      type="text"
                      id={`name-${seatId}`}
                      value={passenger.name}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], name: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300 focus:ring-opacity-50"
                    />

                    <label className="inline-flex items-center mt-2">
                      <input
                        type="checkbox"
                        checked={mainPassenger === seatId}
                        onChange={() => handleMainPassengerChange(seatId)}
                        className="form-checkbox text-blue-600"
                      />
                      <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                    </label>

                    {mainPassenger === seatId && (
                      <div className="space-y-4 mt-4">
                        <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                          Email
                        </label>
                        <input
                          type="email"
                          id={`email-${seatId}`}
                          value={passenger.email}
                          onChange={(e) =>
                            setPassengers((prev) => ({
                              ...prev,
                              [seatId]: { ...prev[seatId], email: e.target.value },
                            }))
                          }
                          required
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300 focus:ring-opacity-50"
                        />

                        <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700">
                          Phone Number
                        </label>
                        <input
                          type="tel"
                          id={`phone-${seatId}`}
                          value={passenger.phone}
                          onChange={(e) =>
                            setPassengers((prev) => ({
                              ...prev,
                              [seatId]: { ...prev[seatId], phone: e.target.value },
                            }))
                          }
                          required
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300 focus:ring-opacity-50"
                        />
                      </div>
                    )}
                  </div>
                ))}

                {error && <div className="text-red-600 text-sm font-medium">{error}</div>}

                {selectedSeats.length === 0 && (
                  <div className="bg-yellow-100 text-yellow-700 border border-yellow-400 rounded-md p-2 text-center">
                    Please select the seats for the trip.
                  </div>
                )}

                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className={`w-40 py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium ${selectedSeats.length === 0
                        ? 'text-gray-400 bg-gray-200 cursor-not-allowed'
                        : 'text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'
                      }`}
                    disabled={selectedSeats.length === 0}
                  >
                    Book Seats
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        <div className="w-full md:w-[30%] shadow-md rounded-lg overflow-hidden">
          <TripDetailsCard />

          <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
            <div className="bg-gray-100 px-4 py-2">
              <h2 className="text-lg font-semibold">Select Seats</h2>
            </div>
            <div className="p-4">
              <div className="grid grid-rows-3 grid-cols-2 gap-4">
                {seats.map((seat) => (
                  <div key={seat.id} className="flex flex-col items-center">
                    <button
                      onClick={() =>
                        !seat.isDisabled && !seat.isBooked && handleSeatClick(seat.id)
                      }
                      className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${seat.isSelected
                        ? 'bg-blue-600 text-white'
                        : seat.isBooked
                          ? 'bg-red-600 text-white'
                          : 'bg-white text-blue-600 border-2 border-blue-600'
                        } ${seat.isDisabled ? 'cursor-not-allowed' : ''}`}
                    >
                      {seat.id}
                    </button>
                    {seat.isBooked && <span className="text-xs text-red-600 mt-2">Booked</span>}
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="text-center my-6">
            <h2 className="text-xl font-semibold">Total Amount: ₹{totalAmount}</h2>
            <button
              onClick={handlePayment}
              className="mt-4 bg-green-500 text-white py-2 px-6 rounded-full shadow-md hover:bg-green-600"
            >
              Pay Now
            </button>
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-10">
          <div className="bg-white p-8 rounded-md shadow-md w-96">
            <h2 className="text-xl font-semibold text-center mb-4">Booking Successful</h2>
            <p className="text-center mb-4">Thank you for booking your seats.</p>
            <button
              onClick={closeModal}
              className="bg-blue-600 text-white px-4 py-2 rounded-md w-full"
            >
              Go Back to Seats
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default SeatList;
