import React, { useState } from 'react';
import { IoIosSearch } from "react-icons/io";
import { IoSearchOutline } from "react-icons/io5";
import { Link } from 'react-router-dom';
import Logo from "../Images/lion.png"; // Your logo path

const Header_for_mobile = () => {
  const [isOpen, setIsOpen] = useState(false);  // State to handle menu toggle

  return (
    <div>
      {/* Header container with rounded bottom corners */}
      <div className="w-full h-24 bg-[#757170] rounded-b-3xl flex items-center justify-between px-4 shadow-md">
        {/* Logo section */}
        <div className="flex items-center">
          <img src={Logo} className="w-auto h-10" alt="Logo" />
        </div>

        {/* Search Bar Section */}
        <div className="flex items-center bg-white rounded-full px-3 py-2 shadow-md w-3/4">
          <IoIosSearch className="text-gray-600" />
          <input
            type="text"
            placeholder="Search here..."
            className="ml-2 w-full text-sm outline-none bg-transparent"
          />
        </div>

        {/* Menu Icon for mobile */}
        <button
          onClick={() => setIsOpen(!isOpen)} // Toggle the menu
          className="lg:hidden text-white"
        >
          {isOpen ? (
            <IoSearchOutline className="text-2xl" />
          ) : (
            <IoIosSearch className="text-2xl" />
          )}
        </button>
      </div>

      {/* Mobile Menu */}
      <div
        className={`${
          isOpen ? 'translate-x-0 opacity-100' : '-translate-x-full opacity-0'
        } absolute inset-x-0 z-20 w-full px-6 py-4 transition-all duration-300 ease-in-out bg-white shadow-md lg:hidden`}
      >
        <div className="flex flex-col items-center">
          <Link to="/" className="py-2 px-4 text-gray-700 hover:bg-gray-100 rounded-md w-full text-center">Home</Link>
          <Link to="/productGroup" className="py-2 px-4 text-gray-700 hover:bg-gray-100 rounded-md w-full text-center">Products</Link>
          <Link to="/cart" className="py-2 px-4 text-gray-700 hover:bg-gray-100 rounded-md w-full text-center">Cart</Link>
          <Link to="/login" className="py-2 px-4 text-gray-700 hover:bg-gray-100 rounded-md w-full text-center">Login</Link>
        </div>
      </div>
    </div>
  );
};

export default Header_for_mobile;
