import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import SelectedCarDetails from './SelectedCarDetails';
import TripDetailsCard from '../Listpage/TripDetailsCard';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleInputChange = (seatId, field, value) => {
    setPassengers((prevPassengers) => ({
      ...prevPassengers,
      [seatId]: { ...prevPassengers[seatId], [field]: value },
    }));
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger(seatId);
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!mainPassenger) {
      setError('Please select a Main Passenger.');
      return;
    }

    setError(null);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats');
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <>
      <div className="mx-auto flex flex-col md:flex-row gap-8 p-4 w-11/12 md:w-[85%]">
        {/* Passenger Details Form */}
        <div className="w-full md:w-[70%] bg-white shadow-md rounded-lg overflow-hidden">
          <SelectedCarDetails />
          <div className="p-4">
            <form onSubmit={handleSubmit} className="space-y-4">
              {selectedSeats.map((seat) => (
                <div key={seat.id}>
                  <h3 className="text-lg font-medium text-gray-800">
                    Passenger Details for Seat {seat.id}
                  </h3>
                  <div className="space-y-2">
                    <input
                      type="text"
                      className="block w-full px-3 py-2 border rounded-md shadow-sm"
                      placeholder="Enter name"
                      value={passengers[seat.id]?.name || ''}
                      onChange={(e) =>
                        handleInputChange(seat.id, 'name', e.target.value)
                      }
                      disabled={!seat.isSelected}
                    />
                    <input
                      type="email"
                      className="block w-full px-3 py-2 border rounded-md shadow-sm"
                      placeholder="Enter email"
                      value={passengers[seat.id]?.email || ''}
                      onChange={(e) =>
                        handleInputChange(seat.id, 'email', e.target.value)
                      }
                      disabled={!seat.isSelected || !passengers[seat.id]?.isMain}
                    />
                    <input
                      type="tel"
                      className="block w-full px-3 py-2 border rounded-md shadow-sm"
                      placeholder="Enter phone"
                      value={passengers[seat.id]?.phone || ''}
                      onChange={(e) =>
                        handleInputChange(seat.id, 'phone', e.target.value)
                      }
                      disabled={!seat.isSelected || !passengers[seat.id]?.isMain}
                    />
                    <label className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={passengers[seat.id]?.isMain || false}
                        onChange={() => handleMainPassengerChange(seat.id)}
                        disabled={!seat.isSelected}
                      />
                      <span>Set as Main Passenger</span>
                    </label>
                  </div>
                </div>
              ))}
              <button
                type="submit"
                className="w-full bg-blue-600 text-white px-4 py-2 rounded-md"
                disabled={selectedSeats.length === 0}
              >
                Book Seats
              </button>
            </form>
          </div>
        </div>

        {/* Seats Section */}
        <div className="w-full md:w-[30%] shadow-md rounded-lg overflow-hidden">
          <TripDetailsCard />
          <div className="p-4">
            <div className="grid grid-cols-2 gap-4">
              {seats.map((seat) => (
                <button
                  key={seat.id}
                  onClick={() => !seat.isDisabled && handleSeatClick(seat.id)}
                  className={`p-4 border rounded-lg ${seat.isSelected ? 'bg-blue-500 text-white' : ''}`}
                  disabled={seat.isDisabled || seat.isBooked}
                >
                  {seat.id}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg">
            <h2 className="text-xl font-bold mb-4">Booking Details</h2>
            <p>Total Amount: Rs. {totalAmount}</p>
            <button onClick={closeModal} className="mt-4 bg-blue-500 text-white p-2 rounded">
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default SeatList;
