import React, { useState } from 'react';
import { FaCar } from "react-icons/fa6";
import Crysta from "../Images/cryy.jpg";
import Ertiga from "../Images/Ertiga.jpeg";
import Xuv from "../Images/Xuv500.jpg";

function SeatSelectionModal({ seats, onSelect, onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h3 className="text-xl font-bold text-gray-800 mb-4">Select Your Seat</h3>
        <div className="grid grid-cols-4 gap-4">
          {seats.map((seat, index) => (
            <button
              key={index}
              disabled={!seat.available}
              onClick={() => onSelect(seat.number)}
              className={`py-2 px-3 rounded-md text-sm font-medium ${
                seat.available
                  ? 'bg-green-500 text-white hover:bg-green-600'
                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
            >
              {seat.number}
            </button>
          ))}
        </div>
        <button
          onClick={onClose}
          className="mt-4 w-full bg-red-500 text-white py-2 px-4 rounded-md shadow hover:bg-red-600"
        >
          Close
        </button>
      </div>
    </div>
  );
}

function Cab({ name, features, seatsLeft, price, image, onSelectSeat }) {
  return (
    <div className="bg-gradient-to-r from-blue-50 via-white to-blue-100 p-6 rounded-lg shadow-lg mb-6 transition transform hover:scale-105 sm:flex sm:justify-between sm:items-start">
      <div className="sm:w-1/4 flex-shrink-0">
        <img
          src={image}
          alt={name}
          className="w-full h-32 sm:h-40 object-cover rounded-lg shadow-md"
        />
      </div>
      <div className="sm:w-3/4 sm:ml-6">
        <h3 className="text-xl sm:text-2xl font-extrabold text-gray-800">{name}</h3>
        <ul className="mt-3 space-y-1">
          {features.map((feature, index) => (
            <li
              key={index}
              className="text-sm sm:text-base text-gray-700 flex items-center"
            >
              <span className="mr-2 text-blue-500">✔</span>
              {feature}
            </li>
          ))}
        </ul>
        <p className="text-sm sm:text-base text-gray-700 mt-2">Seats Left: {seatsLeft}</p>
      </div>
      <div className="text-right sm:w-1/4 sm:ml-4">
        <p className="text-2xl sm:text-3xl font-bold text-blue-600">₹{price}/-</p>
        <button
          onClick={onSelectSeat}
          className="mt-4 bg-gradient-to-r from-green-400 to-green-500 text-white py-2 px-6 rounded-md shadow-md hover:bg-green-600 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-green-300 w-full sm:w-auto"
        >
          Select Seat
        </button>
        <p className="mt-3 text-sm sm:text-base text-gray-600">6 Seater</p>
      </div>
    </div>
  );
}

const CabDetails = () => {
  const [cabType, setCabType] = useState('shared');
  const [selectedCab, setSelectedCab] = useState(null);
  const [showSeatModal, setShowSeatModal] = useState(false);
  const [availableSeats, setAvailableSeats] = useState([]);

  const cabs = [
    {
      name: 'Toyota Innova',
      features: [
        'Air Conditioned Luxury SUV',
        'Comfortable & Spacious Seating',
        'Female Passenger Safety Standards',
      ],
      seatsLeft: 5,
      price: 1200,
      image: Crysta,
      seats: [
        { number: 1, available: true },
        { number: 2, available: true },
        { number: 3, available: false },
        { number: 4, available: true },
        { number: 5, available: true },
      ],
    },
    {
      name: 'Maruti Suzuki Ertiga',
      features: [
        'Fuel Efficient MPV',
        'Comfortable Seating for 7',
        '24/7 Roadside Assistance',
      ],
      seatsLeft: 3,
      price: 1000,
      image: Ertiga, // actual image URL
      seats: [
        { number: 1, available: true },
        { number: 2, available: true },
        { number: 3, available: false },
        { number: 4, available: true },
        { number: 5, available: true },
      ],
    },
    {
      name: 'Mahindra XUV500',
      features: [
        'Powerful SUV for Hill Drives',
        'Advanced Safety Features',
        'Panoramic Sunroof',
      ],
      seatsLeft: 4,
      price: 1500,
      image: Xuv, // Replace with actual image URL
      seats: [
        { number: 1, available: true },
        { number: 2, available: true },
        { number: 3, available: false },
        { number: 4, available: true },
        { number: 5, available: true },
      ],
    },
  ];

  const handleSelectSeat = (cab) => {
    setSelectedCab(cab);
    setAvailableSeats(cab.seats);
    setShowSeatModal(true);
  };

  const handleSeatSelection = (seatNumber) => {
    console.log(`Seat ${seatNumber} selected for ${selectedCab.name}`);
    setShowSeatModal(false);
  };

  return (
    <div className="mt-10 mx-auto max-w-6xl px-4">
      <div className="flex flex-col sm:flex-row justify-between items-center mb-6">
        <h2 className="text-2xl sm:text-3xl font-bold text-gray-800 mb-4 sm:mb-0">
          Available Cabs
        </h2>
        <div className="flex space-x-4">
          <button
            className={`py-2 px-6 rounded-full font-medium text-sm sm:text-base transition ${
              cabType === 'shared'
                ? 'bg-blue-600 text-white shadow-lg'
                : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
            }`}
            onClick={() => setCabType('shared')}
          >
            Shared Cars
          </button>
          <button
            className={`py-2 px-6 rounded-full font-medium text-sm sm:text-base transition ${
              cabType === 'full'
                ? 'bg-blue-600 text-white shadow-lg'
                : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
            }`}
            onClick={() => setCabType('full')}
          >
            Full Cars
          </button>
        </div>
      </div>
      {cabs.map((cab, index) => (
        <Cab
          key={index}
          {...cab}
          onSelectSeat={() => handleSelectSeat(cab)}
        />
      ))}
      {showSeatModal && (
        <SeatSelectionModal
          seats={availableSeats}
          onSelect={handleSeatSelection}
          onClose={() => setShowSeatModal(false)}
        />
      )}
    </div>
  );
};

export default CabDetails;
