import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BookingForm from '../Home/bookingForm';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger(seatId);
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true); // Show the modal on form submission
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats'); // Navigate to the Seats page
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <div className="flex flex-col md:flex-row gap-8 p-4">
      {/* Booking Form */}
      <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
        <div className="bg-gray-100 px-4 py-2">
          <h2 className="text-lg font-semibold">Booking Information</h2>
        </div>
        <BookingForm className="mt-0" buttonLabel="Modify" />
        <div className="p-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            {Object.entries(passengers).map(([seatId, passenger]) => (
              <div key={seatId} className="space-y-2">
                <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                  Passenger Name for Seat {seatId}
                </label>
                <input
                  type="text"
                  id={`name-${seatId}`}
                  value={passenger.name}
                  onChange={(e) =>
                    setPassengers((prev) => ({
                      ...prev,
                      [seatId]: { ...prev[seatId], name: e.target.value },
                    }))
                  }
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                />
                <label className="inline-flex items-center mt-2">
                  <input
                    type="radio"
                    name="mainPassenger"
                    value={seatId}
                    checked={mainPassenger === seatId}
                    onChange={() => handleMainPassengerChange(seatId)}
                    className="form-radio text-blue-600"
                  />
                  <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                </label>
              </div>
            ))}
            {selectedSeats.length > 0 && (
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Book Seats
              </button>
            )}
          </form>
        </div>
      </div>

      {/* Select Seats */}
      <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
        <div className="bg-gray-100 px-4 py-2">
          <h2 className="text-lg font-semibold">Select Seats</h2>
        </div>
        <div className="p-4">
          <div className="grid grid-rows-3 grid-cols-2 gap-4">
            {seats.map((seat) => (
              <div key={seat.id} className="flex flex-col items-center">
                <button
                  onClick={() =>
                    !seat.isDisabled && !seat.isBooked && handleSeatClick(seat.id)
                  }
                  className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${
                    seat.isSelected
                      ? 'bg-blue-600 text-white'
                      : seat.isBooked
                      ? 'bg-red-600 text-white'
                      : 'bg-white text-blue-600 border-2 border-blue-600'
                  } ${
                    seat.isDisabled || seat.isBooked
                      ? 'opacity-50 cursor-not-allowed'
                      : 'hover:bg-blue-100'
                  }`}
                  disabled={seat.isDisabled || seat.isBooked}
                >
                  {seat.id}
                </button>
                {!seat.isBooked && !seat.isDisabled && (
                  <span className="mt-2 text-sm text-gray-600">Rs. {seat.price}</span>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white w-3/4 max-w-lg rounded-lg shadow-lg p-6 relative">
            {/* Close Button */}
            <button
              onClick={closeModal}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
            >
              &times;
            </button>

            <h3 className="text-lg font-semibold mb-4">Booking Details</h3>
            <p>From Location: Delhi</p>
            <p>To Location: Mumbai</p>
            <p>Pickup Location: Rajiv Chowk</p>
            <p>Drop Location: Andheri West</p>
            <p>Date of Trip: {new Date().toLocaleDateString()}</p>
            <p>
              Seats Booked: {selectedSeats.map((seat) => seat.id).join(', ')}
            </p>
            <p>Total Amount Payable: Rs. {totalAmount}</p>
            <button className="mt-4 py-2 px-4 bg-green-500 text-white rounded-md shadow">
              Pay Now
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatList;
