import React, { useState } from "react";
import Rubicon from "../Images/wrangler.jpeg";
import TripDetailsCard from "../Listpage/TripDetailsCard";

const SelectedFullCabDetails = () => {
    const [formData, setFormData] = useState({
        name: "",
        phone: "",
        altPhone: "",
        email: "",
        fromDate: "",
        toDate: "",
    });

    const [errors, setErrors] = useState({});
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedCar, setSelectedCar] = useState(null);

    const today = new Date().toISOString().split("T")[0];

    const validateForm = () => {
        let newErrors = {};
        if (!formData.name.trim()) newErrors.name = "Name is required";
        if (!formData.phone) newErrors.phone = "Phone number is required";
        else if (!/^\d{10}$/.test(formData.phone))
            newErrors.phone = "Enter a valid 10-digit phone number";
        if (!formData.altPhone) newErrors.altPhone = "Alternate phone number is required";
        else if (!/^\d{10}$/.test(formData.altPhone))
            newErrors.altPhone = "Enter a valid 10-digit phone number";
        else if (formData.phone === formData.altPhone)
            newErrors.altPhone = "Alternate phone number should be different";
        if (!formData.email) newErrors.email = "Email is required";
        else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
            newErrors.email = "Enter a valid email address";
        if (!formData.fromDate) newErrors.fromDate = "From date is required";
        if (!formData.toDate) newErrors.toDate = "To date is required";
        else if (formData.toDate < formData.fromDate)
            newErrors.toDate = "To date should be after From date";

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        setErrors({ ...errors, [name]: "" });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            setSelectedCar(cars[0]); // Assuming only one car is being booked
            setIsModalOpen(true);
        }
    };

    const closeModal = () => setIsModalOpen(false);

    const calculateTotalDays = () => {
        if (!formData.fromDate || !formData.toDate) return 0;
        const fromDate = new Date(formData.fromDate);
        const toDate = new Date(formData.toDate);
        return Math.ceil((toDate - fromDate) / (1000 * 60 * 60 * 24)) + 1;
    };

    const cars = [
        {
            name: "Jeep Rubicon",
            category: "Premium SUV",
            image: Rubicon,
            seats: 6,
            price: 13000,
            discount: 15,
            originalPrice: 15000,
            transmission: "Manual",
            hasAC: true,
            rating: 4.7,
            reviews: 189,
            features: [
                "Free Cancellation",
                "Third Party Liability",
                "Instantly Confirmed",
                "Air Conditioned Luxury SUV",
                "Comfortable & Spacious Seating",
                "Female Passenger Safety Standards",
            ],
        },
    ];

    return (
        <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
            <div className="w-full lg:w-2/3">
                {cars.map((car, index) => (
                    <div
                        key={index}
                        className="bg-white rounded-lg mt-6 shadow-md p-4 mb-4 flex flex-col sm:flex-row gap-4"
                    >
                        <img
                            src={car.image || "/placeholder.svg"}
                            alt={car.name}
                            className="w-full sm:w-1/3 object-cover rounded-lg"
                        />
                        <div className="flex flex-col sm:flex-row justify-between w-full">
                            <div className="flex-grow">
                                <h3 className="text-lg sm:text-xl font-bold text-gray-800">
                                    {car.name}
                                </h3>
                                <p className="text-gray-900 mb-2 text-sm sm:text-base">
                                    {car.seats} Seater
                                </p>
                                <ul className="text-gray-700 text-sm mb-4">
                                    {car.features.map((feature, idx) => (
                                        <li key={idx}>• {feature}</li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    </div>
                ))}

                <div className="w-full mx-auto mt-10">
                    <form className="bg-white p-6 md:p-8 shadow-lg rounded-lg w-full" onSubmit={handleSubmit}>
                        <h2 className="text-xl md:text-2xl font-semibold text-center text-gray-900 mb-6">
                            Booking Details
                        </h2>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700">Name</label>
                                <input type="text" name="name" value={formData.name} onChange={handleChange} className="w-full mt-1 p-3 border border-gray-300 rounded-md" />
                                {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
                            </div>
                            <div>
                                <label className="block text-sm font-medium text-gray-700">Phone Number</label>
                                <input type="tel" name="phone" value={formData.phone} onChange={handleChange} className="w-full mt-1 p-3 border border-gray-300 rounded-md" />
                                {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
                            </div>
                        </div>

                        <div className="text-center mt-6">
                            <button type="submit" className="w-full bg-blue-600 text-white px-6 py-3 text-sm font-medium rounded-md shadow">
                                Book Car
                            </button>
                        </div>
                    </form>

                    {isModalOpen && selectedCar && (
                        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
                            <div className="bg-white p-6 md:p-8 rounded-lg shadow-lg w-full max-w-md">
                                <h3 className="text-xl font-semibold mb-4">Booking Summary</h3>
                                <p><strong>Name:</strong> {formData.name}</p>
                                <p><strong>Phone Number:</strong> {formData.phone}</p>
                                <p><strong>Car:</strong> {selectedCar.name}</p>
                                <p><strong>Category:</strong> {selectedCar.category}</p>
                                <p><strong>Seats:</strong> {selectedCar.seats}</p>
                                <p><strong>From Date:</strong> {formData.fromDate}</p>
                                <p><strong>To Date:</strong> {formData.toDate}</p>
                                <p><strong>Total Days:</strong> {calculateTotalDays()}</p>
                                <p><strong>Total Amount:</strong> ₹{calculateTotalDays() * selectedCar.originalPrice}</p>
                                
                                <button onClick={closeModal} className="mt-4 w-full bg-red-500 text-white px-6 py-2 rounded-md">
                                    Close
                                </button>
                                <button className="mt-2 w-full bg-green-500 text-white px-6 py-2 rounded-md">
                                    Pay Now
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            <div className="w-full lg:w-1/3 rounded-lg p-4">
                <TripDetailsCard />
            </div>
        </div>
    );
};

export default SelectedFullCabDetails;
