import React, { useState } from "react";
import CarLayout from "../Images/car-layout.png"; // Replace with the actual car layout image.

function SeatSelectionModal({ seats, onSelect, onClose }) {
  const [selectedSeat, setSelectedSeat] = useState(null);

  const handleSeatClick = (seat) => {
    if (seat.available) {
      setSelectedSeat(seat.number);
      onSelect(seat.number);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md relative">
        <button
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-500 hover:text-red-500 focus:outline-none"
        >
          &times;
        </button>
        <h3 className="text-xl font-bold text-gray-800 text-center">Select Your Seat</h3>
        <p className="text-gray-500 text-sm text-center mt-1">
          All seats are front-facing
        </p>

        {/* Car Layout */}
        <div className="relative mt-4">
          <img
            src={CarLayout}
            alt="Car Layout"
            className="w-full h-auto rounded-md shadow-md"
          />
          <div className="absolute top-8 left-10 grid grid-cols-2 gap-4 w-[60%] mx-auto">
            {seats.map((seat, index) => (
              <button
                key={index}
                onClick={() => handleSeatClick(seat)}
                disabled={!seat.available}
                className={`w-12 h-12 flex items-center justify-center rounded-md font-bold text-sm border ${
                  seat.number === selectedSeat
                    ? "bg-green-500 text-white"
                    : seat.available
                    ? "bg-gray-100 text-black hover:bg-green-100"
                    : "bg-red-300 text-white cursor-not-allowed"
                }`}
                style={{
                  gridColumn: seat.position.column,
                  gridRow: seat.position.row,
                }}
              >
                {seat.number}
              </button>
            ))}
          </div>
        </div>

        {/* Legend */}
        <div className="mt-6 flex flex-col items-start text-sm text-gray-700 space-y-2">
          <div className="flex items-center">
            <span className="w-4 h-4 bg-gray-100 border border-gray-400 rounded-full inline-block mr-2"></span>
            Available Seat
          </div>
          <div className="flex items-center">
            <span className="w-4 h-4 bg-green-500 rounded-full inline-block mr-2"></span>
            Selected Seat
          </div>
          <div className="flex items-center">
            <span className="w-4 h-4 bg-black rounded-full inline-block mr-2"></span>
            Driver's Seat
          </div>
          <div className="flex items-center">
            <span className="w-4 h-4 bg-red-300 rounded-full inline-block mr-2"></span>
            Booked Seat
          </div>
        </div>

        {/* Proceed Button */}
        <button
          onClick={onClose}
          className="mt-4 w-full bg-green-600 text-white py-2 px-4 rounded-md shadow hover:bg-green-700 focus:outline-none"
          disabled={!selectedSeat}
        >
          Proceed
        </button>
      </div>
    </div>
  );
}

export default SeatSelectionModal;
