import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BookingForm from '../Home/bookingForm';
import SelectedCarDetails from './SelectedCarDetails';
import TripDetailsCard from '../Listpage/TripDetailsCard';

const SeatList = () => {
  const [seats, setSeats] = useState([
    { id: 'L1', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R1', isSelected: false, isDisabled: true, isBooked: false, price: 1200 },
    { id: 'L2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'R2', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
    { id: 'L3', isSelected: false, isDisabled: false, isBooked: true, price: 1200 },
    { id: 'R3', isSelected: false, isDisabled: false, isBooked: false, price: 1200 },
  ]);

  const [passengers, setPassengers] = useState({});
  const [mainPassenger, setMainPassenger] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState(null); // State for error message
  const navigate = useNavigate();


  const handlePayment = async () => {
    // Check if Razorpay script is loaded
    if (!window.Razorpay) {
      alert('Razorpay is not loaded properly!');
      return;
    }

    const response = await fetch('http://localhost:5000/api/payments/create-order', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ totalAmount: totalAmount }), // Pass the amount here
    });

    const order = await response.json();

    if (!order || !order.id) {
      alert('Failed to create order');
      return;
    }

    const options = {
      key: process.env.REACT_APP_RAZORPAY_KEY_ID, // Your Razorpay key
      amount: totalAmount * 100,
      currency: 'INR',
      name: 'The CabS',
      description: 'Booking Details',
      order_id: order.id,
      handler: async (response) => {
        const paymentDetails = {
          paymentId: response.razorpay_payment_id,
          orderId: response.razorpay_order_id,
          signature: response.razorpay_signature,
          paymentDetails: {
            method: 'Razorpay',
            status: 'success',
            amount: totalAmount,
            mainPassenger: passengers[mainPassenger],
            seatsBooked: selectedSeats,
            tripDetails: {
              fromLocation: 'Delhi',
              toLocation: 'Mumbai',
              pickupLocation: 'Rajiv Chowk',
              dropLocation: 'Andheri West',
              dateOfTrip: new Date(),
            }
          }
        };

        const captureResponse = await fetch('http://localhost:5000/api/payments/capture-payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(paymentDetails),
        });

        const result = await captureResponse.json();
        if (result.success) {
          alert('Payment successful');
        } else {
          alert('Payment failed');
        }
      },
      prefill: {
        name: 'Customer Name',
        email: 'customer@example.com',
        contact: '1234567890',
      },
      theme: {
        color: '#F37254',
      },
    };

    const rzp1 = new window.Razorpay(options);  // Razorpay constructor
    rzp1.open();
  };

  const handleSeatClick = (id) => {
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
      )
    );

    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      if (updatedPassengers[id]) {
        delete updatedPassengers[id];
      } else {
        updatedPassengers[id] = { name: '', email: '', phone: '', isMain: false };
      }
      return updatedPassengers;
    });
  };

  const handleMainPassengerChange = (seatId) => {
    setMainPassenger((prevMain) => (prevMain === seatId ? null : seatId));
    setPassengers((prevPassengers) => {
      const updatedPassengers = { ...prevPassengers };
      Object.keys(updatedPassengers).forEach((id) => {
        updatedPassengers[id].isMain = id === seatId && seatId !== mainPassenger;
      });
      return updatedPassengers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate that a main passenger is selected
    if (!mainPassenger) {
      setError("There Should be an One Main Passenger ! Please Fill.");
      return;
    }

    setError(null); // Clear any previous error

    setShowModal(true); // Show the modal on form submission
  };

  const closeModal = () => {
    setShowModal(false);
    navigate('/Seats'); // Navigate to the Seats page
  };

  const selectedSeats = seats.filter((seat) => seat.isSelected);
  const totalAmount = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  return (
    <div className="flex flex-col md:flex-row gap-8 p-4">
      {/* Display message when no seats are selected */}
      {/* {selectedSeats.length === 0 && (
      <div className="bg-yellow-100 text-yellow-700 border border-yellow-400 rounded-md p-4 text-center">
        Please select the seat for the trip.
      </div>
    )} */}

      {/* Booking Form */}
      <div className="flex-col shadow-md rounded-lg overflow-hidden w-1/4">
        {/* <SelectedCarDetails/> */}
        <div className="bg-gray-100 px-4 py-2">
          {/* <h2 className="text-lg font-semibold">Booking Information</h2> */}
        </div>
        {/* <BookingForm className="mt-0" buttonLabel="Modify" /> */}
        <TripDetailsCard />
        <div className="p-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            {Object.entries(passengers).map(([seatId, passenger]) => (
              <div key={seatId} className="space-y-2">
                <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                  Passenger Name for Seat {seatId}
                </label>
                <input
                  type="text"
                  id={`name-${seatId}`}
                  value={passenger.name}
                  onChange={(e) =>
                    setPassengers((prev) => ({
                      ...prev,
                      [seatId]: { ...prev[seatId], name: e.target.value },
                    }))
                  }
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                />
                <label className="inline-flex items-center mt-2">
                  <input
                    type="checkbox"
                    checked={mainPassenger === seatId}
                    onChange={() => handleMainPassengerChange(seatId)}
                    className="form-checkbox text-blue-600"
                  />
                  <span className="ml-2 text-sm text-gray-700">Set as Main Passenger</span>
                </label>
                {mainPassenger === seatId && (
                  <div className="mt-2">
                    <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                      Email
                    </label>
                    <input
                      type="email"
                      id={`email-${seatId}`}
                      value={passenger.email}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], email: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                    />
                    <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700 mt-2">
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      id={`phone-${seatId}`}
                      value={passenger.phone}
                      onChange={(e) =>
                        setPassengers((prev) => ({
                          ...prev,
                          [seatId]: { ...prev[seatId], phone: e.target.value },
                        }))
                      }
                      required
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                    />
                  </div>
                )}
              </div>
            ))}

            {/* Error Message */}
            {error && (
              <div className="text-red-600 text-sm font-medium">
                {error}
              </div>
            )}

            {/* No Seats Selected Message */}
            {selectedSeats.length === 0 && (
              <div className="bg-yellow-100 text-yellow-700 border border-yellow-400 rounded-md p-2 text-center">
                Please select the seats for the trip.
              </div>
            )}

            {selectedSeats.length > 0 && (
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Book Seats
              </button>
            )}
          </form>
        </div>
        

      </div>

      {/* Select Seats */}
      <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
        <div className="bg-gray-100 px-4 py-2">
          <h2 className="text-lg font-semibold">Select Seats</h2>
        </div>
        <div className="p-4">
          <div className="grid grid-rows-3 grid-cols-2 gap-4">
            {seats.map((seat) => (
              <div key={seat.id} className="flex flex-col items-center">
                <button
                  onClick={() =>
                    !seat.isDisabled && !seat.isBooked && handleSeatClick(seat.id)
                  }
                  className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${seat.isSelected
                      ? 'bg-blue-600 text-white'
                      : seat.isBooked
                        ? 'bg-red-600 text-white'
                        : 'bg-white text-blue-600 border-2 border-blue-600'
                    } ${seat.isDisabled || seat.isBooked
                      ? 'opacity-50 cursor-not-allowed'
                      : 'hover:bg-blue-100'
                    }`}
                  disabled={seat.isDisabled || seat.isBooked}
                >
                  {seat.id}
                </button>
                {!seat.isBooked && !seat.isDisabled && (
                  <span className="mt-2 text-sm text-gray-600">Rs. {seat.price}</span>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white w-11/12 max-w-3xl rounded-xl shadow-2xl p-8 relative animate-fade-in">
            {/* Close Button */}
            <button
              onClick={closeModal}
              className="absolute top-3 right-3 text-gray-700 hover:text-gray-900 bg-gray-200 rounded-full p-2 shadow transition hover:scale-110"
            >
              &times;
            </button>

            <h3 className="text-2xl font-bold text-gray-800 mb-6 border-b pb-2">
              Booking Details
            </h3>

            {/* Main Passenger Details */}
            {mainPassenger && passengers[mainPassenger] && (
              <div className="bg-gray-100 p-4 rounded-md mb-6">
                <h4 className="text-xl font-semibold text-gray-700 mb-3">
                  Main Passenger Details
                </h4>
                <p className="text-gray-600">
                  <span className="font-semibold">Name:</span> {passengers[mainPassenger].name}
                </p>
                <p className="text-gray-600">
                  <span className="font-semibold">Email:</span> {passengers[mainPassenger].email}
                </p>
                <p className="text-gray-600">
                  <span className="font-semibold">Phone:</span> {passengers[mainPassenger].phone}
                </p>
              </div>
            )}

            {/* Other Passengers Details */}
            {Object.entries(passengers)
              .filter(([seatId]) => seatId !== mainPassenger)
              .map(([seatId, passenger]) => (
                <div key={seatId} className="bg-gray-50 p-4 rounded-md mb-4">
                  <h4 className="text-lg font-semibold text-gray-700 mb-2">
                    Passenger for Seat {seatId}
                  </h4>
                  <p className="text-gray-600">
                    <span className="font-semibold">Name:</span> {passenger.name || 'N/A'}
                  </p>
                  {/* <p className="text-gray-600">
              <span className="font-semibold">Email:</span> {passenger.email || 'N/A'}
            </p>
            <p className="text-gray-600">
              <span className="font-semibold">Phone:</span> {passenger.phone || 'N/A'}
            </p> */}
                </div>
              ))}

            <div className="space-y-3">
              <p className="text-gray-600">
                <span className="font-semibold">From Location:</span> Delhi
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">To Location:</span> Mumbai
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Pickup Location:</span> Rajiv Chowk
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Drop Location:</span> Andheri West
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Date of Trip:</span> {new Date().toLocaleDateString()}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Seats Booked:</span>{' '}
                {selectedSeats.map((seat) => seat.id).join(', ')}
              </p>
              <p className="text-gray-600 text-lg font-medium">
                <span className="font-semibold">Total Amount Payable:</span> Rs. {totalAmount}
              </p>
            </div>

            <button onClick={handlePayment} className="mt-6 w-full py-3 px-6 bg-blue-600 text-white text-lg font-medium rounded-lg shadow-md hover:bg-blue-700 transition duration-300">
              Pay Now
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatList;
