import React, { useState } from 'react';
import { 
  Button, 
  TextField, 
  Checkbox, 
  FormControlLabel, 
  Card, 
  CardContent, 
  CardHeader, 
  Typography, 
  Grid, 
  Box
} from '@mui/material';
import BookingForm from '../Home/bookingForm';

const SeatList = () => {
    const [seats, setSeats] = useState([
        { id: 1, isSelected: false, isDisabled: true },
        { id: 2, isSelected: false, isDisabled: false },
        { id: 3, isSelected: false, isDisabled: false },
        { id: 4, isSelected: false, isDisabled: false },
        { id: 5, isSelected: false, isDisabled: false },
        { id: 6, isSelected: false, isDisabled: false },
      ]);
    
      const [passengers, setPassengers] = useState({});
      const [mainPassenger, setMainPassenger] = useState(null);
    
      const handleSeatClick = (id) => {
        setSeats(seats.map(seat => 
          seat.id === id ? { ...seat, isSelected: !seat.isSelected } : seat
        ));
    
        setPassengers(prevPassengers => {
          const updatedPassengers = { ...prevPassengers };
          if (updatedPassengers[id]) {
            delete updatedPassengers[id];
          } else {
            updatedPassengers[id] = { name: '', isMain: false };
          }
          return updatedPassengers;
        });
      };
    
      const handlePassengerChange = (seatId, field, value) => {
        setPassengers(prevPassengers => ({
          ...prevPassengers,
          [seatId]: { ...prevPassengers[seatId], [field]: value }
        }));
      };
    
      const handleMainPassengerChange = (seatId) => {
        if (mainPassenger === seatId) {
          setMainPassenger(null);
          setPassengers(prevPassengers => ({
            ...prevPassengers,
            [seatId]: { ...prevPassengers[seatId], isMain: false }
          }));
        } else {
          if (mainPassenger) {
            setPassengers(prevPassengers => ({
              ...prevPassengers,
              [mainPassenger]: { ...prevPassengers[mainPassenger], isMain: false }
            }));
          }
          setMainPassenger(seatId);
          setPassengers(prevPassengers => ({
            ...prevPassengers,
            [seatId]: { ...prevPassengers[seatId], isMain: true }
          }));
        }
      };
    
      const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted:', passengers);
        // Here you would typically send this data to your backend
      };
  return (
    <div className="flex flex-col md:flex-row gap-8 p-4">
    <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
      <div className="bg-gray-100 px-4 py-2">
        <h2 className="text-lg font-semibold">Booking Information</h2>
      </div>
      <BookingForm className="mt-0"/>
      <div className="p-4">
        <form onSubmit={handleSubmit} className="space-y-4">
          {Object.entries(passengers).map(([seatId, passenger]) => (
            <div key={seatId} className="space-y-2">
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id={`main-${seatId}`}
                  checked={passenger.isMain}
                  onChange={() => handleMainPassengerChange(seatId)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label htmlFor={`main-${seatId}`} className="text-sm font-medium text-gray-700">
                  Main Passenger
                </label>
              </div>
              <label htmlFor={`name-${seatId}`} className="block text-sm font-medium text-gray-700">
                Passenger Name for Seat {seatId}
              </label>
              <input
                type="text"
                id={`name-${seatId}`}
                value={passenger.name}
                onChange={(e) => handlePassengerChange(seatId, 'name', e.target.value)}
                required
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
              />
              {passenger.isMain && (
                <>
                  <label htmlFor={`phone-${seatId}`} className="block text-sm font-medium text-gray-700">
                    Phone
                  </label>
                  <input
                    type="tel"
                    id={`phone-${seatId}`}
                    value={passenger.phone || ''}
                    onChange={(e) => handlePassengerChange(seatId, 'phone', e.target.value)}
                    required
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                  <label htmlFor={`email-${seatId}`} className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    type="email"
                    id={`email-${seatId}`}
                    value={passenger.email || ''}
                    onChange={(e) => handlePassengerChange(seatId, 'email', e.target.value)}
                    required
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                  />
                </>
              )}
            </div>
          ))}
          {Object.keys(passengers).length > 0 && (
            <button
              type="submit"
              className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Book Seats
            </button>
          )}
        </form>
      </div>
    </div>

    <div className="flex-1 bg-white shadow-md rounded-lg overflow-hidden">
      <div className="bg-gray-100 px-4 py-2">
        <h2 className="text-lg font-semibold">Select Seats</h2>
      </div>
      <div className="p-4">
        <div className="grid grid-rows-3 grid-cols-2 gap-4">
          {seats.map((seat) => (
            <div key={seat.id} className="flex flex-col items-center">
              <button
                onClick={() => !seat.isDisabled && handleSeatClick(seat.id)}
                className={`h-16 w-16 rounded-md flex items-center justify-center text-lg font-semibold ${
                  seat.isSelected
                    ? 'bg-blue-600 text-white'
                    : 'bg-white text-blue-600 border-2 border-blue-600'
                } ${
                  seat.isDisabled
                    ? 'opacity-50 cursor-not-allowed'
                    : 'hover:bg-blue-100'
                }`}
                disabled={seat.isDisabled}
              >
                {seat.id}
              </button>
              <span className="mt-2 text-sm text-gray-600">Rs. 1200</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  </div>
  )
}

export default SeatList
