import React, { useState } from 'react'
import Crysta from "../Images/cryy.jpg";
import Creta from "../Images/creta.jpeg";
import Etios from "../Images/etios.png";
import Gwagon from "../Images/Gwagon.jpg";
import Gurkha from "../Images/Gurkha.jpg";
import Xuv from "../Images/Xuv500.jpg";
import Rubicon from "../Images/wrangler.jpeg";
import TripDetailsCard from '../Listpage/TripDetailsCard';
import { Link, useLocation } from "react-router-dom";

const FullcarsCard = () => {

  const [sortConfig, setSortConfig] = useState({ key: null, ascending: true });

  const location = useLocation();
  const cars = [
    {
      name: "Mercedes G-Wagon",
      category: "Premium SUV",
      image: Gwagon,
      seats: 6,
      price: 15000,
      discount: 15,
      originalPrice: 15000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.7,
      reviews: 189,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      name: "Force Gurkha",
      category: "Premium SUV",
      image: Gurkha,
      seats: 6,
      price: 10000,
      discount: 15,
      originalPrice: 12000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.7,
      reviews: 189,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },

    {
      name: "Mahindra XUV500",
      category: "Economy",
      image:
        Xuv,
      seats: 6,
      price: 10000,
      discount: 15,
      originalPrice: 440,
      transmission: "Manual",
      hasAC: true,
      rating: 4.7,
      reviews: 189,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      name: "Innova Crysta",
      category: "Economy",
      image: Crysta,
      price: 9000,
      seats: 6,
      discount: 10,
      originalPrice: 12000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.6,
      reviews: 256,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      name: "Creta Hyundai",
      category: "SUV",
      image: Creta,
      seats: 6,
      price: 8000,
      discount: 10,
      originalPrice: 10000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.8,
      reviews: 168,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      name: "Jeep Rubicon",
      category: "Premium SUV",
      image: Rubicon,
      seats: 6,
      price: 13000,
      discount: 15,
      originalPrice: 15000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.7,
      reviews: 189,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },
    {
      name: "Toyota Etios",
      category: "Compact",
      price: 7000,
      image: Etios,
      seats: 6,
      discount: 20,
      originalPrice: 8000,
      transmission: "Manual",
      hasAC: true,
      rating: 4.5,
      reviews: 147,
      features: ["Free Cancellation", "Third Party Liability", "Instantly Confirmed", "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards"],
    },


  ]



   // Sorting Function
   const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      ascending: prev.key === key ? !prev.ascending : true,
    }));
  };

  // Sort the car data dynamically
  const sortedCars = [...cars].sort((a, b) => {
    if (sortConfig.key) {
      if (sortConfig.key === "category") {
        return sortConfig.ascending
          ? a.category.localeCompare(b.category)
          : b.category.localeCompare(a.category);
      } else {
        return sortConfig.ascending ? a[sortConfig.key] - b[sortConfig.key] : b[sortConfig.key] - a[sortConfig.key];
      }
    }
    return 0;
  });

  return (
    <>

      <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
        {/* Tabs Section */}
        <div className="w-full lg:w-3/4 mx-auto p-4">
      {/* Tabs */}
      <div className="flex justify-center space-x-4 mb-6">
        {[
          { path: "/sharedcabs", label: "Shared Cars" },
          { path: "/fullcars", label: "Full Cars" },
        ].map((tab) => (
          <Link key={tab.path} to={tab.path}>
            <button
              className={`px-6 py-2 rounded-lg font-medium text-white transition-all duration-300 shadow-md ${
                location.pathname === tab.path
                  ? "bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg transform scale-105"
                  : "bg-gradient-to-r from-blue-400 to-blue-500 hover:from-blue-500 hover:to-blue-600"
              }`}
            >
              {tab.label}
            </button>
          </Link>
        ))}
      </div>

      {/* Sorting Options */}
      <div className="flex justify-between bg-gray-100 p-3 shadow-md rounded-lg text-gray-800 font-semibold text-sm sm:text-base">
        {/* Category Dropdown */}
        <select
          className="p-2 border rounded-md focus:outline-none focus:ring focus:ring-indigo-300"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="">All Categories</option>
          {uniqueCategories.map((category, idx) => (
            <option key={idx} value={category}>
              {category}
            </option>
          ))}
        </select>

        {/* Sort by Fare Button */}
        <button
          className="px-4 py-2 bg-indigo-500 text-white rounded-md hover:bg-indigo-600 transition-all"
          onClick={handleSortFare}
        >
          Sort by Fare {sortConfig.ascending ? "▲" : "▼"}
        </button>
      </div>

      {/* Car Cards */}
      {sortedCars.map((car, index) => (
        <div
          key={index}
          className="bg-white bg-opacity-90 backdrop-blur-md rounded-lg shadow-lg p-5 my-4 flex flex-col sm:flex-row gap-6 border border-gray-200 hover:shadow-xl transition-all"
        >
          {/* Car Image */}
          <img
            src={car.image || "/placeholder.svg"}
            alt={car.name}
            className="w-full sm:w-1/3 object-cover rounded-lg shadow-md"
          />

          {/* Car Details */}
          <div className="flex flex-col sm:flex-row justify-between w-full">
            <div className="flex-grow">
              <h3 className="text-xl font-bold text-gray-800">{car.name}</h3>
              <p className="text-gray-600 text-sm sm:text-base">{car.seats} Seater</p>
              <ul className="text-gray-500 text-sm mt-2">
                {car.features.map((feature, idx) => (
                  <li key={idx}>• {feature}</li>
                ))}
              </ul>
            </div>

            {/* Additional Car Details */}
            <div className="flex flex-col items-center justify-center text-center">
              <p className="text-lg font-semibold text-indigo-600">{car.date}</p>
              <p className="text-gray-500 text-sm sm:text-base">{car.time}</p>
              <span className="bg-green-300 text-green-800 px-3 py-1 mt-3 rounded-full font-semibold text-sm">
                {car.category}
              </span>
            </div>

            {/* Pricing and Actions */}
            <div className="flex flex-col items-end justify-between text-right">
              <div className="inline-block px-3 py-1 text-blue-600 rounded-full text-sm font-medium mt-1">
                {car.discount}% OFF!
              </div>
              <p className="text-gray-400 line-through text-xl">₹{car.originalPrice}</p>
              <p className="text-2xl font-bold text-blue-600">₹{car.price}/Day</p>

              <Link to="/fullcabbooking">
                <button className="mt-4 bg-gradient-to-r from-blue-400 to-blue-600 text-white py-2 px-5 rounded-md shadow-lg hover:from-blue-500 hover:to-blue-700 transition-all">
                  Book Now
                </button>
              </Link>
            </div>
          </div>
        </div>
      ))}
    </div>

        {/* Trip Details Section */}
        <div className="w-full lg:w-1/4 rounded-lg p-4">
          <TripDetailsCard />
        </div>
      </div>


    </>


  )
}

export default FullcarsCard
