import React, { useState } from 'react';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: "Dr. Elara Quantum",
    title: "Quantum Computing Specialist",
    email: "elara.quantum@techfusion.com",
    phone: "1234567890",
    oldPassword: "",
    newPassword: "",
    avatar: "./images/cryy.jpg",
  });
  const [errors, setErrors] = useState({});

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle avatar image change
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({ ...prev, avatar: reader.result }));
      };
      reader.readAsDataURL(file);
    } else {
      alert("Please select a valid image file.");
    }
  };

  // Validate form data
  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = "Name is required.";
    if (!formData.email.trim()) newErrors.email = "Email is required.";
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Invalid email format.";
    if (!formData.phone.trim()) newErrors.phone = "Phone number is required.";
    else if (!/^\d{10}$/.test(formData.phone)) newErrors.phone = "Phone number must be 10 digits.";
    if (formData.oldPassword && !formData.newPassword) newErrors.newPassword = "New password is required.";
    if (formData.newPassword && !formData.oldPassword) newErrors.oldPassword = "Old password is required.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("Form submitted:", formData);
      setIsEditing(false);

      // Placeholder for API submission
      // You can integrate your API call here
    }
  };

  const handleLogout = () => {
    // Add logout functionality here
    console.log("User logged out.");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-slate-200 p-4 flex items-center justify-center">
      <div className="bg-white rounded-3xl shadow-2xl overflow-hidden w-full max-w-5xl flex flex-col md:flex-row">
        {/* Left Profile Section */}
        <div className="md:w-1/3 bg-gradient-to-br from-teal-500 to-cyan-600 p-8 text-white flex flex-col items-center justify-center relative">
          <div className="relative w-32 h-32 rounded-full border-4 border-white shadow-lg mb-4 overflow-hidden group">
            <img
              className="w-full h-full object-cover"
              src={formData.avatar || "/placeholder.svg"}
              alt={`${formData.name}'s avatar`}
            />
            {isEditing && (
              <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                <label htmlFor="avatar-upload" className="cursor-pointer text-white text-sm font-semibold">
                  Change Photo
                </label>
                <input
                  id="avatar-upload"
                  type="file"
                  className="hidden"
                  accept="image/*"
                  onChange={handleAvatarChange}
                />
              </div>
            )}
          </div>
          <h2 className="text-2xl font-bold mb-1">{formData.name}</h2>
          <p className="text-sm opacity-75 mb-4">{formData.title}</p>
          <div className="flex space-x-2">
            <button
              onClick={() => setIsEditing(!isEditing)}
              className="px-4 py-2 bg-white text-teal-600 rounded-full text-sm font-semibold hover:bg-opacity-90"
            >
              {isEditing ? "Cancel" : "Edit Profile"}
            </button>
            <button
              onClick={handleLogout}
              className="px-4 py-2 bg-teal-700 text-white rounded-full text-sm font-semibold hover:bg-teal-800"
            >
              Logout
            </button>
          </div>
        </div>

        {/* Right Form Section */}
        <div className="md:w-2/3 p-8">
          <h3 className="text-2xl font-bold text-gray-800 mb-6">Professional Profile</h3>
          <form onSubmit={handleSubmit} className="space-y-6">
            {[
              { id: "name", label: "Name", type: "text" },
              { id: "title", label: "Title", type: "text" },
              { id: "email", label: "Email", type: "email" },
              { id: "phone", label: "Phone", type: "tel" },
            ].map(({ id, label, type }) => (
              <div className="relative" key={id}>
                <input
                  type={type}
                  id={id}
                  name={id}
                  value={formData[id]}
                  onChange={handleChange}
                  disabled={!isEditing}
                  className={`peer w-full px-4 py-2 border-2 rounded-lg placeholder-transparent focus:outline-none ${
                    isEditing ? "border-teal-300 focus:border-teal-500" : "border-gray-200 bg-gray-50 cursor-not-allowed"
                  } ${errors[id] ? "border-red-500" : ""}`}
                  placeholder={label}
                />
                <label
                  htmlFor={id}
                  className={`absolute left-2 -top-2.5 bg-white px-1 text-sm transition-all ${
                    isEditing ? "text-teal-500" : "text-gray-500"
                  }`}
                >
                  {label}
                </label>
                {errors[id] && <p className="mt-1 text-sm text-red-500">{errors[id]}</p>}
              </div>
            ))}

            {isEditing && (
              <>
                <div className="relative">
                  <input
                    type="password"
                    id="oldPassword"
                    name="oldPassword"
                    value={formData.oldPassword}
                    onChange={handleChange}
                    className={`peer w-full px-4 py-2 border-2 rounded-lg ${
                      errors.oldPassword ? "border-red-500" : "border-teal-300 focus:border-teal-500"
                    }`}
                    placeholder="Old Password"
                  />
                  <label htmlFor="oldPassword" className="absolute left-2 -top-2.5 bg-white px-1 text-sm text-teal-500">
                    Old Password
                  </label>
                  {errors.oldPassword && <p className="mt-1 text-sm text-red-500">{errors.oldPassword}</p>}
                </div>
                <div className="relative">
                  <input
                    type="password"
                    id="newPassword"
                    name="newPassword"
                    value={formData.newPassword}
                    onChange={handleChange}
                    className={`peer w-full px-4 py-2 border-2 rounded-lg ${
                      errors.newPassword ? "border-red-500" : "border-teal-300 focus:border-teal-500"
                    }`}
                    placeholder="New Password"
                  />
                  <label htmlFor="newPassword" className="absolute left-2 -top-2.5 bg-white px-1 text-sm text-teal-500">
                    New Password
                  </label>
                  {errors.newPassword && <p className="mt-1 text-sm text-red-500">{errors.newPassword}</p>}
                </div>
              </>
            )}
            {isEditing && (
              <div className="flex justify-end">
                <button
                  type="submit"
                  className="px-6 py-2 bg-teal-600 text-white rounded-lg font-semibold text-sm uppercase hover:bg-teal-700"
                >
                  Save Changes
                </button>
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Profile;
