import React, { useState } from "react";
import { Link } from "react-router-dom";

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone_number: "",
  });
  const [agreeToTerms, setAgreeToTerms] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [showAlert, setShowAlert] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTermsModalOpen, setIsTermsModalOpen] = useState(false);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [id]: value,
    }));
  };

  const handleTermsChange = (e) => {
    setAgreeToTerms(e.target.checked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.phone_number) {
      setAlertMessage("Please fill out all fields.");
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 5000);
      return;
    }

    if (!agreeToTerms) {
      setAlertMessage("You must agree to the Terms and Conditions.");
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 5000);
      return;
    }

    formData.email = formData?.email.toLowerCase();

    // Open modal with form data
    setIsModalOpen(true);
  };

  const openTermsModal = () => {
    setIsTermsModalOpen(true);
  };

  const closeTermsModal = () => {
    setIsTermsModalOpen(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-blue-50 px-4 sm:px-6 lg:px-8 py-10">
      {/* Alert */}
      {showAlert && (
        <div className="absolute top-5 w-full max-w-sm">
          <div
            className={`${
              alertMessage.includes("successfully")
                ? "bg-green-200 border-green-400 text-green-800"
                : "bg-red-200 border-red-400 text-red-800"
            } border p-4 rounded-lg shadow-lg`}
          >
            <p className="font-semibold">
              {alertMessage.includes("successfully") ? "Success" : "Error"}
            </p>
            <pre className="whitespace-pre-wrap text-sm">{alertMessage}</pre>
          </div>
        </div>
      )}

      {/* Signup Form */}
      <div className="w-full max-w-md md:max-w-lg bg-white p-6 sm:p-8 md:p-10 rounded-xl shadow-lg">
        <h2 className="text-2xl md:text-3xl font-bold text-blue-600 text-center mb-6 md:mb-8">
          Create Your Account
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Name Field */}
          <div>
            <label
              htmlFor="name"
              className="block text-sm md:text-base font-medium text-gray-700"
            >
              Full Name
            </label>
            <input
              type="text"
              id="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full mt-2 p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="John Doe"
            />
          </div>

          {/* Email Field */}
          <div>
            <label
              htmlFor="email"
              className="block text-sm md:text-base font-medium text-gray-700"
            >
              Email Address
            </label>
            <input
              type="email"
              id="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full mt-2 p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="example@mail.com"
            />
          </div>

          {/* Phone Number Field */}
          <div>
            <label
              htmlFor="phone_number"
              className="block text-sm md:text-base font-medium text-gray-700"
            >
              Phone Number
            </label>
            <input
              type="tel"
              id="phone_number"
              value={formData.phone_number}
              onChange={handleChange}
              className="w-full mt-2 p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="+1234567890"
            />
          </div>

          {/* Terms and Conditions */}
          <div className="flex items-start">
            <input
              type="checkbox"
              id="terms"
              checked={agreeToTerms}
              onChange={handleTermsChange}
              className="w-5 h-5 mt-1 mr-3"
            />
            <label htmlFor="terms" className="text-sm text-gray-700">
              I agree to the{" "}
              <button
                type="button"
                onClick={openTermsModal}
                className="text-blue-500 font-medium hover:underline"
              >
                Terms and Conditions
              </button>
            </label>
          </div>

          {/* Submit Button */}
          <div>
            <button
              type="submit"
              className="w-full bg-blue-500 text-white py-3 rounded-lg font-semibold text-lg shadow-md hover:bg-blue-600 transition duration-300"
            >
              Sign Up
            </button>
          </div>
        </form>
      </div>

      {/* Modal for Terms and Conditions */}
      {isTermsModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white w-full max-w-4xl p-6 rounded-lg shadow-lg">
            <h1 className="text-2xl font-bold text-center mb-6">
              Terms and Conditions
            </h1>
            <div className="text-gray-700 space-y-4 mb-6">
              <p>
                Welcome to our platform! By accessing or using our services, you agree to the following terms and conditions:
              </p>
              <ol className="list-decimal pl-5 space-y-2">
                <li><strong>Acceptance of Terms:</strong> By using our platform...</li>
                <li><strong>Use of Service:</strong> You agree to use our platform only for lawful purposes...</li>
                <li><strong>Intellectual Property:</strong> All content...</li>
                <li><strong>Account Security:</strong> You are responsible for maintaining...</li>
                <li><strong>Privacy Policy:</strong> Our Privacy Policy governs...</li>
                <li><strong>Payment and Billing:</strong> Some of our services...</li>
                <li><strong>Termination:</strong> We may suspend or terminate your access...</li>
                <li><strong>Dispute Resolution:</strong> In the event of a dispute...</li>
                <li><strong>Limitation of Liability:</strong> Our liability...</li>
                <li><strong>Changes to Terms:</strong> We reserve the right...</li>
              </ol>
              <p>If you have any questions or concerns...</p>
            </div>
            <div className="flex flex-col items-center">
              <label className="flex items-center mb-4 text-sm text-gray-600">
                <input
                  type="checkbox"
                  checked={agreeToTerms}
                  onChange={(e) => setAgreeToTerms(e.target.checked)}
                  className="mr-2 w-4 h-4 text-blue-500 border-gray-300 rounded focus:ring-blue-400"
                />
                I agree to the Terms and Conditions
              </label>
              <button
                onClick={closeTermsModal}
                disabled={!agreeToTerms}
                className={`px-4 py-2 w-full sm:w-auto bg-blue-500 text-white text-sm font-semibold rounded-lg shadow-md transition duration-200 ${
                  agreeToTerms
                    ? "hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75"
                    : "bg-gray-300 cursor-not-allowed"
                }`}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal with Form Data */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white w-full max-w-md p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold text-gray-700 mb-4">
              Signup Successful!
            </h3>
            <div className="space-y-2">
              <p className="text-gray-600"><strong>Name:</strong> {formData.name}</p>
              <p className="text-gray-600"><strong>Email:</strong> {formData.email}</p>
              <p className="text-gray-600"><strong>Phone Number:</strong> {formData.phone_number}</p>
            </div>
            <button
              onClick={closeModal}
              className="mt-6 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-md hover:bg-blue-600 transition duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Signup;
